import Router from 'next/router'
import { getTokenFromStorage } from './authentication-service';

async function  Guards () {
    const token = await getTokenFromStorage();
    console.log('hjk',token)
    if(token.role !== 'admin'){
        Router.push('/index');
    }else {
        return;
    }
}

export default Guards;
