import { Toast } from 'react-bootstrap'

const ShowToast = props => {
    return (
        <div
            style={{
                position: 'absolute',
                top: '40%',
                right: '40%',
                zIndex: '100'
            }}
        >
            <Toast show={props.show} delay={100} animation autohide style={{
                display: 'absolute',
                background: 'black',

            }}>
                <Toast.Header>
                    <img
                        src="muhalik.jpg"
                        className="rounded mr-2"
                        alt=""
                    />
                </Toast.Header>
                <Toast.Body> Product Uploaded Successfully</Toast.Body>
            </Toast>
        </div>
    )
}

export default ShowToast;
