import { Toast } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp } from '@fortawesome/free-solid-svg-icons';

const ShowToast = props => {
    return (
        <div
            style={{
                position: 'absolute',
                top: '40%',
                right: '40%',
                zIndex: '100'
            }}
        >
            <Toast  show={props.show} delay={100} animation="true" autohide  style={{
                display: 'absolute',
                background: 'gray',

            }}>
                <Toast.Header>
                    <FontAwesomeIcon icon={faThumbsUp} style={styles.fontawesome}></FontAwesomeIcon>
                    <span style={{color: 'green'}}>Success</span>
                </Toast.Header>
                <Toast.Body> Product Uploaded Successfully</Toast.Body>
            </Toast>
        </div>
    )
}

export default ShowToast;


const styles = {
    fontawesome: {
        color: 'green',
        width: '15px',
        height: '15px',
        maxHeight: '15px',
        maxWidth: '15px',
    },
}
