import { Row, Col, Image, Container, Carousel } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus, faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft } from '@fortawesome/free-regular-svg-icons';
import GlobalStyleSheet from '../../../../../../../styleSheet';
import React, { Component } from 'react';
// https://drive.google.com/uc?export=view&id=1NXjE17yYJj2Pt7be9_J0DXdsnNzO4Yd4
// https://drive.google.com/uc?export=view&id=1F7ylPEh_UmvimCgPAOM7rf6gJ_yG8TK9

function ControlledCarousel(props) {
    const [index, setIndex] = React.useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    return (
        <Carousel activeIndex={index} onSelect={handleSelect}>
            <Carousel.Item>
                {props.image && props.image.map((element, index) =>
                    <img
                        className="d-block w-100"
                        src={element.value}
                        alt="First slide"
                    />
                )}
                <Carousel.Caption>
                    <h3>image: {index + 1} </h3>
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    );
}

const ViewProduct = props => {

    return (
        <>
            <Row style={styles.title_row} noGutters>
                <FontAwesomeIcon className="fontawesome" icon={faArrowLeft} style={styles.title_fontawesome} onClick={props.back} className="mr-auto" />
                <div className="mr-auto" style={styles.title}> {props.data.product_name}</div>
                <div style={styles.title}> Prev </div>
                <div style={styles.title}> Next </div>
            </Row>
            <Row style={styles.img_row} noGutters>
                {/* <Col lg={3} md={3}> */}
                {/* <div className="mr-auto"></div> */}
                {/* </Col> */}
                <Col >
                    {/* <Row noGutters>
                        {props.data.product_image_link && props.data.product_image_link.map((element =>
                            <div style={{ margin: '0%', padding: '0%' }}>
                                <Image thumbnail fluid style={{ minWidth: '250px', maxWidth: '250px' }}
                                    src={element.value} alt="Flowers in Chania" />
                            </div>
                        ))}
                    </Row> */}
                    <ControlledCarousel
                        image={props.data.product_image_link}
                    />
                </Col>
                {/* <Col lg={3} md={3}> */}
                {/* <div className="mr-auto"></div> */}
                {/* </Col> */}
            </Row>
            <style jsx>
                {`
                    .fontawesome: hover{
                        background: red;
                        color: blue;
                    }
                `}
            </style>
        </>
    )
}

const styles = {
    title_row: {
        borderBottom: '1px solid gray',
        padding: '0.7% 4%',
        display: 'flex',
        alignItems: 'center'
    },
    title_fontawesome: {
        cursor: 'pointer',
        marginRight: '3%',
        width: '20px',
        height: '20px',
        maxHeight: '20px',
        maxWidth: '20px',
    },
    title: {
        color: `${GlobalStyleSheet.admin_primry_color}`,
    },
    img_row: {
        margin: '2% 5%'
    },
}
export default ViewProduct;
