import { OverlayTrigger, UpdatingPopover } from 'react-bootstrap'

const longContent = `
  Very long
  Multiline content
  that is engaging and what-not
`;
const shortContent = 'Short and sweet!';

function OverlayPopover(props) {
    const [content, setContent] = useState(shortContent);

    useEffect(() => {
        const timerId = setInterval(() => {
            setContent(content === shortContent ? longContent : shortContent);
        }, 3000);

        return () => clearInterval(timerId);
    });

    return (
        <OverlayTrigger
            trigger="click"
            overlay={
                <UpdatingPopover id="popover-contained">{content}</UpdatingPopover>
            }
        >
            {props.children}
        </OverlayTrigger>
    );
}

export default OverlayPopover
