                                                </Nav.Link>
                                    </div>
                                </Nav.Item>
                                <Nav.Item>
                                    <div className="hover">
                                        <Nav.Link eventKey="five" style={styles.tab_link}>
                                            <FontAwesomeIcon icon={faWarehouse} style={styles.fontawesome} />
                                            Inventory
                                                </Nav.Link>
                                    </div>
                                </Nav.Item>
                                <Nav.Item>
                                    <div className="hover">
                                        <Nav.Link eventKey="six" style={styles.tab_link}>
                                            <FontAwesomeIcon icon={faTags} style={styles.fontawesome} />
                                            Discounts
                                                </Nav.Link>
                                    </div>
                                </Nav.Item>
                                <Nav.Item>
                                    <div className="hover">
                                        <Nav.Link eventKey="seven" onClick={this.boxClick} style={{color: this.state.clr}}>
                                            <FontAwesomeIcon icon={faPercent} style={styles.fontawesome} />
                                            Commision
                                                </Nav.Link>
                                    </div>
                                </Nav.Item>
                                <Nav.Item>
                                    <div className="hover" onClick={this.boxClick}>
                                        <Nav.Link eventKey="eight" style={styles.tab_link}>
                                            <FontAwesomeIcon icon={faChartBar} style={styles.fontawesome} />
                                            Reports
                                    </Nav.Link>
                                    </div>
                                </Nav.Item>
                            </Nav>
                        </div>
                        <div as={Col} lg={9} md={9} sm={10} className="tab_content">
                            <Tab.Content>
                                <Tab.Pane eventKey="one">
                                    <Vendors />
                                </Tab.Pane>
                                <Tab.Pane eventKey="two">
                                    <Customers />
                                </Tab.Pane>
                                <Tab.Pane eventKey="three">
                                    <Products />
                                </Tab.Pane>
                                <Tab.Pane eventKey="four">
                                    <ProductCategories />
                                </Tab.Pane>
                                <Tab.Pane eventKey="five">
                                    <Inventory />
                                </Tab.Pane>
                                <Tab.Pane eventKey="six">
                                    <Discounts />
                                </Tab.Pane>
                                <Tab.Pane eventKey="seven">
                                    <Commision />
                                </Tab.Pane>
                                <Tab.Pane eventKey="eight">
                                    <Reports />
                                </Tab.Pane>
                            </Tab.Content>
                        </div>
                        {/* End of the Tabs Components */}
                    </Row>
                </Tab.Container>
                <style jsx>
                    {`
                .hover {
                    margin: 2px 0px;
                    background: ${GlobalStyleSheet.admin_primry_color};
                    color: ${GlobalStyleSheet.body_color};
                }
                .hover:hover {
                    margin-left: 5px;
                    margin-right: -5px;
                    margin-top: -2px;
                    margin-bottom: 6px;
                }
                .hover:focus {
                    background:red;
                }
                @media (max-width: 991px) {
                    .tabs {
                      display: none;
                    }
                    .tab_content {
                        display: none;
                    }
                }
                @media (min-width: 992px) {
                    .side_tab_toogle_btn {
                        display: none;
                    }
                }
            `}
                </style>
            </div>
        )
    }
}

