import React, { Component } from 'react';
import AdminLayout from './layouts/AdminLayout';
import AdminStyleSheet from './components/style-sheets/adminStyleSheet';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons';

import { Nav, Form, FormControl, Button, Spinner, Tab, Row, Col, } from "react-bootstrap"

import Vendors from './components/dashboards/admin-dashboard/vendors';
import Customers from './components/dashboards/admin-dashboard/customers';
import Products from './components/dashboards/admin-dashboard/products';
import ProductCategories from './components/dashboards/admin-dashboard/product-categories';
import Inventory from './components/dashboards/admin-dashboard/inventory';
import Discounts from './components/dashboards/admin-dashboard/discount';
import Commision from './components/dashboards/admin-dashboard/commision';
import Reports from './components/dashboards/admin-dashboard/reports';

const BackDrop = props => (
    <div>
        <div onClick={props.click}>
        </div>
        <style jsx>{`
             position: fixed;
             width: 100%;
             height: 100%;
             top: 0;
             left: 0;
             background: rgba(0, 0, 0, 0.3);
             z-index: 100;
        `}</style>
    </div>
)

class AdminDashboard extends Component {
    state = {
        sideDrawerOpen: false
    };

    drawerToggleClickHandler = () => {
        this.setState(prevState => {
            return { sideDrawerOpen: !prevState.sideDrawerOpen };
        });
    };

    backdropClickHandler = () => {
        this.setState({ sideDrawerOpen: false });
    };


    render() {
        let drawerClasses = "tabs_side_drawer";
        let backdrop;

        if (this.state.sideDrawerOpen) {
            drawerClasses = "tabs_side_drawer open";
        }
        if (this.state.sideDrawerOpen) {
            backdrop = <BackDrop click={this.backdropClickHandler} />;
        }

        return (
            <div>
                <AdminLayout>
                    <Tab.Container id="dashboard-tabs" defaultActiveKey="one">
                        {/* Toogle Button Components */}
                        <div className="side_tab_toogle_btn" style={{ width: '100%' }}>
                            <Form inline>
                                <Button onClick={this.drawerToggleClickHandler}>
                                    <FontAwesomeIcon icon={faSearch} />
                                </Button>
                                <FormControl type="text" placeholder="Search" className="mr-sm-6" style={{ flex: '1', margin: '0px 5px' }}></FormControl>
                                <Button>
                                    <FontAwesomeIcon icon={faSearch} />
                                </Button>
                            </Form>
                        </div>
                        {/* End of the Toogle Button Components */}
                        <Row style={{ padding: '0px', margin: '0px' }}>

                            {/* Tabs Components */}
                            <div className="tabs">
                                <Col sm={2} style={{ padding: '0px', maxWidth: '280px', minWidth: '110px' }}>
                                    <Nav className="flex-column" variant="pills">
                                        <Form inline>
                                            <FormControl style={AdminStyleSheet.search_bar} type="text" placeholder="Search" className="mr-sm-6"></FormControl>
                                            <Button style={AdminStyleSheet.search_btn}>
                                                <FontAwesomeIcon icon={faSearch} />
                                            </Button>
                                        </Form>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="one" style={AdminStyleSheet.link_color}>Vendors</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="two" style={AdminStyleSheet.link_color}> Customers</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="three" style={AdminStyleSheet.link_color}>Products</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="four" style={AdminStyleSheet.link_color}>Categories</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="five" style={AdminStyleSheet.link_color}>Inventory</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="six" style={AdminStyleSheet.link_color}>Discounts</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="seven" style={AdminStyleSheet.link_color}>Commision</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="eight" style={AdminStyleSheet.link_color}>Reports</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                    </Nav>
                                </Col>
                            </div>
                            <div className="tab_content">
                                <Col sm={"auto"} style={{ padding: '5px', margin: '0px', minWidth: '100%' }}>
                                    <Tab.Content>
                                        <Tab.Pane eventKey="one">
                                            <Vendors />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="two">
                                            <Customers />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="three">
                                            <Products />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="four">
                                            <ProductCategories />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="five">
                                            <Inventory />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="six">
                                            <Discounts />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="seven">
                                            <Commision />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="eight">
                                            <Reports />
                                        </Tab.Pane>
                                    </Tab.Content>
                                </Col>
                            </div>
                            {/* End of the Tabs Components */}



                            {/* Side Drawer Components */}
                            <div className={drawerClasses}>
                                <div>
                                    <Nav className="flex-column" variant="pills">
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="one" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>Vendors</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="two" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}> Customers</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="three" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>Products</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="four" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>Categories</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="five" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>Inventory</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="six" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>Discounts</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="seven" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>Commision</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="eight" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>Reports</Nav.Link>
                                            </div>
                                        </Nav.Item>
                                    </Nav>
                                </div>
                            </div>

                            <div className="tabs_side_drawer_tab_content">
                                <Col sm={"auto"} style={{ padding: '0px' }}>
                                    <Tab.Content>
                                        <Tab.Pane eventKey="one">
                                            <Vendors />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="two">
                                            <Customers />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="three">
                                            <Products />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="four">
                                            <ProductCategories />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="five">
                                            <Inventory />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="six">
                                            <Discounts />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="seven">
                                            <Commision />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="eight">
                                            <Reports />
                                        </Tab.Pane>
                                    </Tab.Content>
                                </Col>
                            </div>
                            {/* End of the Side Drawer Components */}

                            {backdrop}

                        </Row>
                    </Tab.Container>
                </AdminLayout>
                <style jsx>
                    {`
                .hover:hover {
                    margin-left: 10px;
                    margin-right: -10px;
                    margin-top: -4px;
                    margin-bottom: 4px;
                }
                .tabs_side_drawer{
                    height: 100%;
                    background: white;
                    box-shadow: 1px 0px 7px rgba(0, 0, 0, 0.5);
                    position: fixed;
                    top: 100;
                    left: 0;
                    width: 60%;
                    max-width: 400px;
                    z-index: 200;
                    transform: translateX(-100% );
                    transition: transform 0.5s ease-out;
                }
                .tabs_side_drawer.open{
                    transform: translateX(0);
                }
                @media (max-width: 991px) {
                    .tabs {
                      display: none;
                    }
                    .tab_content {
                        display: none;
                    }
                }
                @media (min-width: 992px) {
                    .side_tab_toogle_btn {
                        display: none;
                    }
                    .tabs_side_drawer {
                        display: none;
                    }
                    .tabs_side_drawer_tab_content {
                        display: none;
                    }
                }

            `}
                </style>
            </div>
        );
    }
}

export default AdminDashboard;
