import React, { Component } from 'react';
import { Navbar, Form, Button, Row, Col, Container, Image } from 'react-bootstrap';

import GlobalStyleSheet from '../styleSheet';
const styles = {
    body: {
        background: `${GlobalStyleSheet.body_color}`,
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        bottom: '0',

    },
    submit_btn: {
        background: `${GlobalStyleSheet.primry_color}`,
        color: `${GlobalStyleSheet.primary_text_color}`,
        // background: 'white',
        border: 'none',
        // marginTop: '50px',
    },
    container: {
        // background: `${GlobalStyleSheet.primry_color}`,
        background: 'white',
        border: `0.5px solid ${GlobalStyleSheet.primry_color}`,
        marginTop: '50px',
        padding: '20px 30px'
    },
    label: {
        width: '100%',
        fontSize: '14px',
    },
}
class Login extends Component {
    state = {}
    render() {
        return (
            <div style={styles.body}>
                <Navbar variant="dark" style={{ background: `${GlobalStyleSheet.primry_color}` }}>
                    <Navbar.Brand href="/" className="mr-auto" > Muhalik    </Navbar.Brand>
                </Navbar>
                <Container>
                    <p>
                        <Image src="/static/muhalik.jpg" roundedCircle thumbnail fluid style={{ width: '25%', maxWidth: '150px' }} />
                    </p>
                    <Row>
                        <Col lg={4} md={3} sm={2} xs={0}></Col>
                        <Col style={styles.container}>
                            <Form>
                                <Form.Group controlId="formBasicEmail">
                                    <Form.Label style={styles.label}>Email address</Form.Label>
                                    <Form.Control type="email" placeholder="Enter email" />
                                    {/* <Form.Text className="text-muted"> We'll never share your email with anyone else. </Form.Text> */}
                                </Form.Group>

                                <Form.Group controlId="formBasicPassword">
                                    <Form.Label style={styles.label}>Password</Form.Label>
                                    <Form.Control type="password" placeholder="Password" />
                                </Form.Group>

                                <Button variant="light" size="sm" block type="submit" style={styles.submit_btn}> Login </Button>
                                <Button variant="light" size="sm" block type="submit" style={styles.submit_btn}> Sighnup </Button>
                            </Form>
                        </Col>
                        <Col lg={4} md={3} sm={2} xs={0}></Col>
                    </Row>
                </Container>
                <style jsx>
                    {`
                        p {
                            text-align: center; 
                            margin: 0px;
                        }
                    `}
                </style>
            </div>
        );
    }
}

export default Login;
