                                <Tab.Pane eventKey="six">
                                    <Discounts />
                                </Tab.Pane>
                                <Tab.Pane eventKey="seven">
                                    <Commision />
                                </Tab.Pane>
                                <Tab.Pane eventKey="eight">
                                    <Reports />
                                </Tab.Pane>
                            </Tab.Content>
                        </div>
                        {/* End of the Tabs Components */}
                    </Row>
                </Tab.Container>
                <style jsx>
                    {`
                .hover {
                    margin: 2px 0px;
                    background: ${GlobalStyleSheet.admin_primry_color};
                    color: ${GlobalStyleSheet.body_color};
                }
                .hover:hover {
                    margin-left: 5px;
                    margin-right: -5px;
                    margin-top: -2px;
                    margin-bottom: 6px;
                }
                .hover:focus {
                    background:red;
                }
                @media (max-width: 991px) {
                    .tabs {
                      display: none;
                    }
                    .tab_content {
                        display: none;
                    }
                }
                @media (min-width: 992px) {
                    .side_tab_toogle_btn {
                        display: none;
                    }
                }
            `}
                </style>
            </div>
        )
    }
}

