import { Navbar, Container, Form, Col, Row, InputGroup, Button } from 'react-bootstrap';
import React, { Component } from 'react';
import { Formik } from 'formik';
import * as yup from 'yup';

import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

import GlobalStyleSheet from '../styleSheet';
const styles = {
    submit_btn: {
        // background: `${GlobalStyleSheet.primry_color}`,
        // background: 'white',
        border: 'none',
        // marginTop: '50px',
    },
    container: {
        background: `${GlobalStyleSheet.primry_color}`,
        // border: `2px solid ${GlobalStyleSheet.primry_color}`,
        marginTop: '50px',
        padding: '20px 30px'
    },
    label: {
        color: `${GlobalStyleSheet.primary_text_color}`,
        marginRight: '10px',
    },
}
const schema = yup.object({
    fullName: yup.string().required(),
    username: yup.string().required(),
    mobile: yup.string().required(),
    verificationCode: yup.string().required(),
    email: yup.string(),
    dob: yup.string().required(),
    gender: yup.string().required(),
    city: yup.string().required(),
});

function SignupForm() {
    return (
        <Formik
            validationSchema={schema}
            onSubmit={console.log}
            initialValues={{
                dob: new Date()
            }}
        >
            {({
                handleSubmit,
                handleChange,
                handleBlur,
                values,
                touched,
                isValid,
                errors,
            }) => (
                    <Form noValidate onSubmit={handleSubmit}>

                        <Form.Row>
                            <Form.Group as={Col} md="6" controlId="validationFormikUsername">
                                <Form.Label>Mobile Number</Form.Label>
                                <InputGroup>
                                    <Form.Control
                                        type="text"
                                        placeholder="Username"
                                        aria-describedby="inputGroupPrepend"
                                        name="username"
                                        value={values.username}
                                        onChange={handleChange}
                                        isInvalid={!!errors.username}
                                    />
                                    <InputGroup.Prepend>
                                        <Button id="inputGroupPrepend">Send Code</Button>
                                    </InputGroup.Prepend>
                                    <Form.Control.Feedback type="invalid">
                                        {errors.username}
                                    </Form.Control.Feedback>
                                </InputGroup>
                            </Form.Group>
                            <Form.Group as={Col} md="6" controlId="validationFormik01">
                                <Form.Label>Full Name</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="firstName"
                                    value={values.firstName}
                                    onChange={handleChange}
                                    isValid={touched.firstName && !errors.firstName}
                                />
                                <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                            </Form.Group>
                        </Form.Row>

                        <Form.Row>
                            <Form.Group as={Col} md="6" controlId="validationFormik03">
                                <Form.Label>Verification Code</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="City"
                                    name="city"
                                    value={values.city}
                                    onChange={handleChange}
                                    isInvalid={!!errors.city}
                                />
                                <Form.Control.Feedback type="invalid">
                                    {errors.city}
                                </Form.Control.Feedback>
                            </Form.Group>
                            <Form.Group as={Col} md="6" controlId="validationFormik03">
                                <Form.Label>Email Address</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="City"
                                    name="city"
                                    value={values.city}
                                    onChange={handleChange}
                                    isInvalid={!!errors.city}
                                />
                                <Form.Control.Feedback type="invalid">
                                    {errors.city}
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Form.Row>

                        <Form.Row>
                            <Col lg={6} md={6}>
                                <Form.Row>
                                    <Form.Group  controlId="validationFormik04">
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control
                                            type="text"
                                            placeholder="State"
                                            name="state"
                                            value={values.state}
                                            onChange={handleChange}
                                            isInvalid={!!errors.state}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {errors.state}
                                        </Form.Control.Feedback>
                                    </Form.Group>
                                </Form.Row>
                                <Form.Row>
                                    <Form.Group size="lg" controlId="validationFormik05">
                                        <Form.Label>Conform Password</Form.Label>
                                        <Form.Control
                                            type="text"
                                            placeholder="Zip"
                                            name="zip"
                                            value={values.zip}
                                            onChange={handleChange}
                                            isInvalid={!!errors.zip}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            {errors.zip}
                                        </Form.Control.Feedback>
                                    </Form.Group>
                                </Form.Row>
                            </Col>
                            <Col lg={6} md={6}>
                                <Form.Group>
                                    <Form.Check
                                        required
                                        name="terms"
                                        label="Agree to terms and conditions"
                                        onChange={handleChange}
                                        isInvalid={!!errors.terms}
                                        feedback={errors.terms}
                                        id="validationFormik0"
                                    />
                                </Form.Group>
                                <Button type="submit">Submit form</Button>
                            </Col>
                        </Form.Row>

                    </Form>
                )}
        </Formik>
    );
}

class Signup extends Component {
    state = {}
    render() {
        return (
            <div>

                <Navbar variant="dark" style={{ background: `${GlobalStyleSheet.primry_color}` }}>
                    <Navbar.Brand href="/" className="mr-auto" > Muhalik    </Navbar.Brand>
                </Navbar>
                <Container style={{ padding: '0px 50px' }}>
                    <Row>
                        <Col lg={1} md={1} sm={1}></Col>
                        <Col lg={10} md={10} sm={10} style={styles.container}>
                            <SignupForm />
                        </Col>
                        <Col lg={1} md={1} sm={1}></Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Signup;
