import React from 'react'
import fetch from 'isomorphic-unfetch'
import Link from 'next/link';
import { Row, Col, Card, Button } from 'react-bootstrap'
// american-sign-language-interpreting
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUpload, faDownload } from '@fortawesome/free-solid-svg-icons';
import { FileSaver , saveAs } from 'file-saver';

import GlobalStyleSheet from '../../../../../styleSheet'

class BulkUpload extends React.Component {
    // fuck(){

    //     var file = new File(["Hello, world!"], "/public/excel.xlsx", {type: "text/plain/ms-excel;charset=utf-8"});
    //     FileSaver.saveAs(file);
    // }
    downloadEmployeeData = () => {
		fetch('/excel.xlsx')
			.then(response => {
				response.blob().then(blob => {
					let url = window.URL.createObjectURL(blob);
					let a = document.createElement('a');
					a.href = url;
					a.download = 'muhalik bulk-upload templete.xlsx';
					a.click();
				});
				//window.location.href = response.url;
		});
	}
    render() {
        return (
            <div >
                <Row style={styles.row}>
                    <h6 className="mr-auto">Products Bulk Upload</h6>
                    <Link href="/index"><a>Home</a></Link>
                </Row>

                <Row style={styles.row}>
                    <Card style={styles.card}>
                        <Card.Header style={{ background: 'skyblue' }}>Download Templete</Card.Header>
                        <Card.Body>
                            <Button variant="primary" size="md" active onClick={this.downloadEmployeeData}>
                                <FontAwesomeIcon icon={faDownload} style={styles.fontawesome} />
                                Download
                            </Button>
                        </Card.Body>
                    </Card>
                </Row>
                <Row style={styles.row}>
                    <Card style={styles.card}>
                        <Card.Header style={{ background: 'skyblue' }}>Upload File</Card.Header>
                        <Card.Body>
                            <div style={styles.browseBtnDiv}>
                                <Button variant="primary" size="sm" style={{marginRight: '10px'}} active>
                                    Browse...
                                </Button>
                                <label id='selectedFile'>No File Selected</label>
                            </div>
                            <div style={{ width: '100%' }}>
                                <Button variant="primary" size="md" active>
                                    <FontAwesomeIcon icon={faUpload} style={styles.fontawesome} />
                                Upload
                            </Button>
                            </div>
                        </Card.Body>
                    </Card>
                </Row>
            </div>
        )
    }
}

const styles = {
    row: {
        margin: '2%',
        padding: '0px'
    },
    card: {
        width: '100%'
    },
    browseBtnDiv: {
        width: '100%',
        padding: '0px 0px 10px 0px'
    },
    fontawesome: {
        color: 'white',
        marginRight: '10px',
        width: '14px',
        height: '14px',
        maxHeight: '14px',
        maxWidth: '14px',
    },
}

export default BulkUpload;
