import React, { Component } from 'react';
import AdminLayout from './components/admin/AdminLayout';
import AdminStyleSheet from './components/admin/adminStyleSheet';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faBars, faPersonBooth, faUsers, faListAlt, faWarehouse, faTags, faPercent, faChartBar } from '@fortawesome/free-solid-svg-icons';
import { faProductHunt } from '@fortawesome/free-brands-svg-icons';

import { Nav, Form, FormControl, Button, Spinner, Tab, Row, Col, } from "react-bootstrap"

import Vendors from './components/admin/admin-dashboard/vendors';
import Customers from './components/admin/admin-dashboard/customers';
import Products from './components/admin/admin-dashboard/products';
import ProductCategories from './components/admin/admin-dashboard/product-categories';
import Inventory from './components/admin/admin-dashboard/inventory';
import Discounts from './components/admin/admin-dashboard/discount';
import Commision from './components/admin/admin-dashboard/commision';
import Reports from './components/admin/admin-dashboard/reports';

const BackDrop = props => (
    <div>
        <div onClick={props.click}>
        </div>
        <style jsx>{`
             position: fixed;
             width: 100%;
             height: 100%;
             top: 0;
             left: 0;
             background: rgba(0, 0, 0, 0.3);
             z-index: 100;
        `}</style>
    </div>
)

class AdminDashboard extends Component {
    state = {
        sideDrawerOpen: false
    };

    drawerToggleClickHandler = () => {
        this.setState(prevState => {
            return { sideDrawerOpen: !prevState.sideDrawerOpen };
        });
    };

    backdropClickHandler = () => {
        this.setState({ sideDrawerOpen: false });
    };


    render() {
        let drawerClasses = "tabs_side_drawer";
        let backdrop;

        if (this.state.sideDrawerOpen) {
            drawerClasses = "tabs_side_drawer open";
        }
        if (this.state.sideDrawerOpen) {
            backdrop = <BackDrop click={this.backdropClickHandler} />;
        }

        return (
            <div>
                <AdminLayout>
                    <Tab.Container id="dashboard-tabs" defaultActiveKey="one">
                        <Row style={{ padding: '0px', margin: '0px' }}>
                            {/* Side Drawer Components */}
                            <div className={drawerClasses}>
                                <div>
                                    <Nav className="flex-column" variant="pills">
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="one" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faPersonBooth} style={{ marginRight: '20px' }} />
                                                    Vendors
                                                </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="two" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faUsers} style={{ marginRight: '20px' }} />
                                                    Customers
                                                </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="three" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faProductHunt} style={{ marginRight: '20px' }} />
                                                    Products
                                                </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="four" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faListAlt} style={{ marginRight: '20px' }} />
                                                    Categories
                                                    </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="five" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faWarehouse} style={{ marginRight: '20px' }} />
                                                    Inventory
                                                </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="six" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faTags} style={{ marginRight: '20px' }} />
                                                    Discounts
                                                </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="seven" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faPercent} style={{ marginRight: '20px' }} />
                                                    Commision
                                                </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                        <Nav.Item>
                                            <div className="hover" style={AdminStyleSheet.border}>
                                                <Nav.Link eventKey="eight" onClick={this.backdropClickHandler} style={AdminStyleSheet.link_color}>
                                                    <FontAwesomeIcon icon={faChartBar} style={{ marginRight: '20px' }} />
                                                    Reports
                                                </Nav.Link>
                                            </div>
                                        </Nav.Item>
                                    </Nav>
                                </div>
                            </div>

                            <div className="tabs_side_drawer_tab_content">
                                <Col sm={"auto"} style={{ padding: '0px' }}>
                                    <Tab.Content>
                                        <Tab.Pane eventKey="one">
                                            <Vendors />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="two">
                                            <Customers />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="three">
                                            <Products />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="four">
                                            <ProductCategories />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="five">
                                            <Inventory />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="six">
                                            <Discounts />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="seven">
                                            <Commision />
                                        </Tab.Pane>
                                        <Tab.Pane eventKey="eight">
                                            <Reports />
                                        </Tab.Pane>
                                    </Tab.Content>
                                </Col>
                            </div>
                            {/* End of the Side Drawer Components */}

                            {backdrop}

                        </Row>
                    </Tab.Container>
                </AdminLayout>
                <style jsx>
                    {`
                .hover:hover {
                    margin-left: 10px;
                    margin-right: -10px;
                    margin-top: -4px;
                    margin-bottom: 4px;
                }
                .tabs_side_drawer{
                    height: 100%;
                    background: white;
                    box-shadow: 1px 0px 7px rgba(0, 0, 0, 0.5);
                    position: fixed;
                    top: 1px;
                    bottom: 1px;
                    left: 0;
                    width: 60%;
                    max-width: 400px;
                    z-index: 200;
                    transform: translateX(-100% );
                    transition: transform 0.5s ease-out;
                }
                .tabs_side_drawer.open{
                    transform: translateX(0);
                }
                @media (max-width: 991px) {
                    .tabs {
                      display: none;
                    }
                    .tab_content {
                        display: none;
                    }
                }
                @media (min-width: 992px) {
                    .side_tab_toogle_btn {
                        display: none;
                    }
