import React, { Component } from 'react';
import { Navbar, Form, Button, Col, Container, Row } from 'react-bootstrap';

import GlobalStyleSheet from '../styleSheet';
const styles = {
    submit_btn: {
        // background: `${GlobalStyleSheet.primry_color}`,
        // background: 'white',
        border: 'none',
        // marginTop: '50px',
    },
    container: {
        background: `${GlobalStyleSheet.primry_color}`,
        // border: `2px solid ${GlobalStyleSheet.primry_color}`,
        marginTop: '50px',
        padding: '20px 30px'
    },
    label: {
        color: `${GlobalStyleSheet.primary_text_color}`,
        marginRight: '10px',
    },
}
class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = {}
    }
    render() {
        function FormExample() {
            const [validated, setValidated] = useState(false);

            const handleSubmit = event => {
                const form = event.currentTarget;
                if (form.checkValidity() === false) {
                    event.preventDefault();
                    event.stopPropagation();
                }

                setValidated(true);
            };
            return (
                <div>
                    <Navbar variant="dark" style={{ background: `${GlobalStyleSheet.primry_color}` }}>
                        <Navbar.Brand href="/" className="mr-auto" > Muhalik    </Navbar.Brand>
                    </Navbar>
                    <Container style={{ padding: '0px 50px' }}>
                        <Row>
                            <Col lg={4} md={3} sm={3}></Col>
                            <Col lg={4} md={6} sm={6} style={styles.container}>
                                <Navbar.Brand href="#" style={styles.label} className="text-center"> Signup    </Navbar.Brand>
                                <Form noValidate validated={validated} onSubmit={handleSubmit}>
                                    <Form.Row>
                                        <Form.Group as={Col} md="4" controlId="validationCustom01">
                                            <Form.Label>First name</Form.Label>
                                            <Form.Control
                                                required
                                                type="text"
                                                placeholder="First name"
                                                defaultValue="Mark"
                                            />
                                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                                        </Form.Group>
                                        <Form.Group as={Col} md="4" controlId="validationCustom02">
                                            <Form.Label>Last name</Form.Label>
                                            <Form.Control
                                                required
                                                type="text"
                                                placeholder="Last name"
                                                defaultValue="Otto"
                                            />
                                            <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                                        </Form.Group>
                                        <Form.Group as={Col} md="4" controlId="validationCustomUsername">
                                            <Form.Label>Username</Form.Label>
                                            <InputGroup>
                                                <InputGroup.Prepend>
                                                    <InputGroup.Text id="inputGroupPrepend">@</InputGroup.Text>
                                                </InputGroup.Prepend>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Username"
                                                    aria-describedby="inputGroupPrepend"
                                                    required
                                                />
                                                <Form.Control.Feedback type="invalid">
                                                    Please choose a username.
            </Form.Control.Feedback>
                                            </InputGroup>
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} md="6" controlId="validationCustom03">
                                            <Form.Label>City</Form.Label>
                                            <Form.Control type="text" placeholder="City" required />
                                            <Form.Control.Feedback type="invalid">
                                                Please provide a valid city.
          </Form.Control.Feedback>
                                        </Form.Group>
                                        <Form.Group as={Col} md="3" controlId="validationCustom04">
                                            <Form.Label>State</Form.Label>
                                            <Form.Control type="text" placeholder="State" required />
                                            <Form.Control.Feedback type="invalid">
                                                Please provide a valid state.
          </Form.Control.Feedback>
                                        </Form.Group>
                                        <Form.Group as={Col} md="3" controlId="validationCustom05">
                                            <Form.Label>Zip</Form.Label>
                                            <Form.Control type="text" placeholder="Zip" required />
                                            <Form.Control.Feedback type="invalid">
                                                Please provide a valid zip.
          </Form.Control.Feedback>
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Group>
                                        <Form.Check
                                            required
                                            label="Agree to terms and conditions"
                                            feedback="You must agree before submitting."
                                        />
                                    </Form.Group>
                                    <Button type="submit">Submit form</Button>
                                </Form>
                            </Col>
                            <Col lg={4} md={3} sm={3}></Col>
                        </Row>
                    </Container>
                </div>
            );
            
        }
    render(<FormExample />);

    }
}
export default Signup;
