const background_color = '#563D7C';
const link_color = 'white';
const vertical_nav_link_color = 'gray';
const AdminStyleSheet = {
    background: {
        background: `${background_color}`,
    },

    // Styles for the dashboard.js
    search_bar: {
        width: '75%',
        flex: '7',
        border: `2px solid ${background_color}`,
        // borderBottom: `2px solid ${background_color}`,
        margin: '2px 2.5px 1px 1.2px',
    },
    search_btn: {
        background: `${background_color}`,
        flex: '1',
        border: 'none',
        color: 'white',
        width: '20%',
        float: 'right',
    },
    border: {
        // border: `5px solid ${background_color}`,
        border: '1px solid white',
        background: `${background_color}`,
        // borderRadius: '20%',
        color: 'white',
    },


    
    nav_link_color: {
        color: `${link_color}`,
    },



    col_text: {
        color: background_color
    },
    
    border_right: {
        borderRight: `5px solid ${background_color}`,
        paddingRight: '2px',
    },



}

export default AdminStyleSheet;
