import React, { Component } from 'react';
class OverlayPopover extends Component {
    state = {}
    render() {
        const popver = (
            <Popover id="popover-basic">
                <Popover.Title as="h3">{this.props.title}</Popover.Title>
                <Popover.Content>
                    {this.props.content}
                </Popover.Content>
            </Popover>
        )

        return (
            <OverlayTrigger trigger="hover" placement="bottom" delay={{ show: 200, hide: 100 }}
                overlay={this.popver}>
                {this.props.children}
            </OverlayTrigger>
        );
    }
}

export default OverlayPopover
