import React, { Component } from 'react';
import { Row, Table, Form } from 'react-bootstrap'
import axios from 'axios'
import MuhalikConfig from '../../../../../../sdk/muhalik.config'
import GlobalStyleSheet from '../../../../../../styleSheet'

class AllProducts extends Component {
    constructor(props) {
        super(props);
        this.state = {
            productsArray: [],
        }
    }
    // Getting Product Categories from DB
    async componentDidMount() {
        const url = MuhalikConfig.PATH + '/api/products/';
        try {
            const response = await axios.get(url);
            console.log('data:', response.data.data.docs)
            this.setState({ productsArray: response.data.data.docs })
        } catch (error) {
            console.log('Data Fetching Eror:', error);
        }
    }

    render() {
        return (
            <>
                <Row noGutters style={{ margin: '2%', background: 'white' }}>
                    <Table striped responsive bordered hover size="sm" style={styles.table}>
                        <thead>
                            <tr>
                                <th >
                                    <Form.Check type="checkbox" />
                                </th>
                                <th>Name</th>
                                <th>SKU</th>
                                <th>Stock</th>
                                <th>Price</th>
                                <th>Categories</th>
                                <th>Tags</th>
                                <th>Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.productsArray && this.state.productsArray.map((element, index) =>
                                <tr>
                                    <td ><Form.Check type="checkbox" /></td>
                                    <td>{element.product_name}</td>
                                    <td>{_.isEmpty(element.product_tags) ? '-' : element.sku}</td>
                                    <td>{element.product_in_stock}</td>
                                    <td>{element.product_price}</td>
                                    <td>{element.product_category[0].value}</td>
                                    <td>{_.isEmpty(element.product_tags) ? '-' : element.product_tags[0].value}</td>
                                    <td>Date</td>
                                </tr>
                            )}
                        </tbody>
                    </Table>
                </Row>
            </>
        );
    }
}

const styles = {
    // table: {
    //     margin: '2%'
    // },
}
export default AllProducts;
