import { Row, Col, Image, Modal, Button, Container } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus, faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft } from '@fortawesome/free-regular-svg-icons';
import GlobalStyleSheet from '../../../../../../../styleSheet';
import React, { Component } from 'react';
// https://drive.google.com/uc?export=view&id=1NXjE17yYJj2Pt7be9_J0DXdsnNzO4Yd4
// https://drive.google.com/uc?export=view&id=1F7ylPEh_UmvimCgPAOM7rf6gJ_yG8TK9


const ViewProduct = props => {
    const [imgPreview, setImgPreview] = React.useState(false)
    const [data, setData] = React.useState('')
    const [index, setIndex] = React.useState('')

    return (
        <>
            <div>
                {imgPreview ?
                    <div className="modal-overlay">
                        <div className="modal-body">
                            <div className="close-modal">
                                <Button className="modal-close" onClick={() => setImgPreview(false)}>
                                    Close
                                </Button>
                            </div>
                            <div className="image-container">
                                <div>
                                    <img
                                        src={data.value}
                                        style={{ width: "100%", dispaly: 'flex' }}
                                    />
                                </div>
                            </div>
                        </div>
                    </div>
                    : null
                }
            </div>
            <Row style={styles.title_row} noGutters>
                <FontAwesomeIcon className="fontawesome" icon={faArrowLeft} style={styles.title_fontawesome} onClick={props.back} className="mr-auto" />
                <div className="mr-auto" style={styles.title}> {props.data.product_name}</div>
                <div style={styles.title}> Prev </div>
                <div style={styles.title}> Next </div>
            </Row>
            <Row style={styles.img_row} noGutters>
                {props.data.product_image_link && props.data.product_image_link.map((element, index) =>
                    <div style={{ margin: '0%' }}>
                        <Image thumbnail fluid style={{ minWidth: '200px', maxWidth: '200px' }}
                            src={element.value} alt="Flowers in Chania" onClick={() => { setImgPreview(true), setData(element), setIndex(index) }} />
                    </div>
                )}
            </Row>
            <style jsx>
                {`
                    .fontawesome: hover{
                        background: red;
                        color: blue;
                    }
                   .modal-overlay {
                        position: fixed;
                        top: 0;
                        left: 0;
                        bottom: 0;
                        right: 0;
                        z-index: 10;
                        width: 100%;
                        height: 100%;
                        background: rgba(21, 21, 21, 0.75);
                    }
                     .modal-body {
                        position: relative;
                        z-index: 11;
                        margin: 3%;
                        overflow: hidden;
                        max-width: 100%;
                        max-height: 100%;
                    }
                    .close-modal {
                        position: fixed;
                        top: 5px;
                        right: 5px;
                    }




                    .button {
                        font-size: 50px;
                        color: #eee;
                        margin: 5px;
                    }
                    .card-icon-open {
                        display: block;
                        position: relative;
                        left: 45%;
                        top: 35px;
                        font-size: 30px;
                        width: 30px;
                        color: #fff;
                        cursor: pointer;
                        opacity: 0;
                        transform: translate(0%, -400%);
                        transition: all 0.25s ease-in-out;
                    }
                    .card-icon-open:focus,
                    .card-icon-open:hover {
                        color: #111;
                    }
                    
                    .fullscreen {
                        position: relative;
                        text-decoration: none;
                        font-size: 25px;
                        color: #eee;
                        z-index: 999;
                    }
                    .fullscreen:hover,
                    .fullscreen:focus,
                    .fullscreen:blur {
                        text-decoration: none;
                        color: red;
                    }
                    .gallery-container {
                        padding-top: 10px;
                    }
                    .gallery-card {
                        position: relative;
                        overflow: hidden;
                        margin-bottom: 10px;
                    }
                    .gallery-thumbnail {
                        max-width: 100%;
                        height: auto;
                        border-radius: 4px;
                    }
                    .gallery-thumbnail:focus ~ .card-icon-open,
                    .gallery-thumbnail:hover ~ .card-icon-open,
                    .gallery-thumbnail ~ .card-icon-open:focus,
                    .gallery-thumbnail ~ .card-icon-open:hover {
                        opacity: 1;
                    }
                    .image-container {
                        display: flex;
                        justify-content: center;
                        align-items: center;
                    }
                    .image-rotate {
                        font-size: 44px;
                    }
                    
                   
                    .modal-close {
                        font-size: 44px;
                        z-index: 99;
                        color: #eee;
                        transition: all 0.25s ease-in-out;
                    }
                    .modal-close:focus,
                    .modal-close:hover {
                        color: red;
                    }
                    .rotate-container {
                        font-size: 44px;
                        position: fixed;
                        top: 0;
                        left: 5px;
                    }
                `}
            </style>
        </>
    )
}

const styles = {
    title_row: {
        borderBottom: '1px solid gray',
        padding: '0.7% 4%',
        display: 'flex',
        alignItems: 'center'
    },
    title_fontawesome: {
        cursor: 'pointer',
        marginRight: '3%',
        width: '20px',
        height: '20px',
        maxHeight: '20px',
        maxWidth: '20px',
    },
    title: {
        color: `${GlobalStyleSheet.admin_primry_color}`,
    },
    img_row: {
        margin: '5%',
        display: 'flex',
        justifyContent: 'center',
        zIndex: '999'
    },
    img: {

    },
}
export default ViewProduct;
