import { Toast } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp } from '@fortawesome/free-solid-svg-icons';

const ShowToast = props => {
    return (
        <div
            style={{
                position: 'absolute',
                top: '40%',
                right: '40%',
                zIndex: '100'
            }}
        >
            <Toast  show={props.show}  delay={3000} autohide style={{
                display: 'absolute',
            }}>
                <Toast.Header>
                    <FontAwesomeIcon icon={faThumbsUp} style={styles.fontawesome}></FontAwesomeIcon>
                    <span style={styles.span}>Success</span>
                </Toast.Header>
                <Toast.Body> Product Uploaded Successfully</Toast.Body>
            </Toast>
        </div>
    )
}

export default ShowToast;


const styles = {
    span: {
        marginLeft: '20px',
        fontSize: '16px',
        color: 'green',
        fontWeight: 'bold'
    },
    fontawesome: {
        color: 'green',
        width: '25px',
        height: '25px',
        maxHeight: '25px',
        maxWidth: '25px',
    },
}
