import GlobalStyleSheet from '../../../../styleSheet';
import { Row, Col, Button, Card } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
    faCcVisa
} from '@fortawesome/free-solid-svg-icons'
const Footer = () => (
    <div>
        <div style={styles.footer}>
            <p className="text-center">
                Copyright@ 2020
                    <span style={{ fontSize: 'medium', fontWeight: 'bold', }}> Muhalik </span>
                - Online Shopping Website
            </p>
            <Row noGutters style={{ padding: '4% 6%' }}>
                <div className='mr-auto'>
                    <div className='list_header'>Services</div>
                    <div className='list_text'>Need Help?</div>
                    <div className='list_text'>Contact Us</div>
                    <div className='list_text'>Submit Issue</div>
                </div>
                <div className='mr-auto'>
                    <div className='list_header'>About Us</div>
                    <div className='list_text'>Know More About Us</div>
                    <div className='list_text'>Muhalik's Team</div>
                </div>

                <div className='mr-auto'>
                    <div className='list_header'>Terms & Conditions</div>
                    <div className='list_text'>Terms</div>
                    <div className='list_text'>Conditions</div>
                    <div className='list_text'>Rules & Regulations</div>
                </div>
                <div>
                    <div className='list_header'>Sell With Us</div>
                    <div className='list_text'>Be Muhalik's Part</div>
                    <div className='list_text'>Learn More</div>
                </div>
            </Row>
            <Card style={styles.card}>
                <Card.Body>
                    <Row>
                        <div className='mr-auto'>
                            <div>Follow Muhalik</div>
                            <Button variant='primary'>Facebook</Button>
                            <Button style={{ background: '#5EAADE' }}>Twitter</Button>
                            <Button style={{ background: 'linear-gradient(to right bottom, #7C48BD, #F15B40)' }}>Instagram</Button>
                        </div>
                        <div className='mr-auto'>
                            <div>Payment Methods</div>
                            <Button variant='outline-secondary' style={{ color: '#202276', fontWeight: 'bold' }}>
                                <FontAwesomeIcon icon={faCcVisa} style={styles.fontawesome} />
                            </Button>
                            <Button variant='outline-secondary' style={{ color: '#202276', fontWeight: 'bold' }}>Visa</Button>
                            <Button variant='outline-secondary' style={{ color: '#202276', fontWeight: 'bold' }}>Visa</Button>
                            <Button variant='outline-secondary' style={{ color: '#202276', fontWeight: 'bold' }}>Visa</Button>
                        </div>
                    </Row>
                </Card.Body>
            </Card>
        </div>
        <style jsx>
            {`
                .list_header {
                    background: none;
                    font-weight: bold;
                    white-space: nowrap;
                    color: ${GlobalStyleSheet.primary_text_color};
                    font-size: 18px;
                    margin-bottom: 8%;
                }
                .list_text {
                    background: none;
                    color: ${GlobalStyleSheet.primary_text_color};
                    font-size: ${GlobalStyleSheet.form_label_fontsize};
                    margin: 3% 0%;
                    white-space: nowrap;
                }
                p {
                    color: white;
                    font-size: ${GlobalStyleSheet.form_label_fontsize}
                }
            `}
        </style>
    </div>
)

const styles = {
    footer: {
        background: `${GlobalStyleSheet.admin_primry_color}`,
        padding: '2% 5%'
    },
    card: {
    },
    fontawesome: {
        color: `${GlobalStyleSheet.primry_color}`,
        margin: '0px 20px 0px 0px',
        width: '18px',
        height: '18px',
        maxHeight: '18px',
        maxWidth: '18px',
    },
}

export default Footer;
