import React, { Component } from 'react';
import { Form, Col, Row, Card, InputGroup, Button, Toast, Tab, Nav, Tabs, Spinner } from 'react-bootstrap';
import { Formik } from 'formik';
import * as yup from 'yup';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
    faPlus, faKey, faSlidersH, faStoreAlt, faTruck, faTools, faDollarSign, faListAlt,
} from '@fortawesome/free-solid-svg-icons';
import Select from 'react-select';
import CreatableSelect from 'react-select/creatable';
import ReactSelectMaterialUi from "react-select-material-ui";
import ShowToast from '../../../../toast';
import GlobalStyleSheet from '../../../../../../styleSheet';
import MuhalikConfig from '../../../../../../sdk/muhalik.config';
import { getUncodededTokenFromStorage } from '../../../../../../sdk/core/authentication-service';
import CustomFields from './add-new-contents/custom-fields';
import product_size_options from '../../../../../../sdk/consts/product-size-options'
import product_color_options from '../../../../../../sdk/consts/product-color-options'

const product_attributes_list = [
    { value: 'Size', label: 'Size' },
    { value: 'Color', label: 'Color' },
    { value: 'Price', label: 'Price' },
]

// Option List for select Product Category (when offline)
const product_category_options = [
    { value: 'Shoe', label: 'Shoe' },
    { value: 'Cloth', label: 'Cloth' },
    { value: 'Shirt', label: 'Shirt' },
    { value: 'Pant', label: 'Pant' }
]

// For React-Select
const components = {
    DropdownIndicator: null,
};
// Model For React-Select
const createOption = (label) => ({
    value: label,
    label,
});

// Yup Schema for validation fields
const schema = yup.object({
    product_type: yup.string(),
    product_weight: yup.number()
        .integer("Enter Only Numbers")
        .min(0, "Must grater than 0 digit")
        .max(100, "Can't be longer than 100"),

    dimension_length: yup.number()
        .integer("Enter Only Numbers")
        .min(0, "Must grater than 0 digit")
        .max(100, "Can't be longer than 100"),

    dimension_width: yup.number()
        .integer("Enter Only Numbers")
        .min(0, "Must grater than 0 digit")
        .max(100, "Can't be longer than 100"),

    dimension_height: yup.number()
        .integer("Enter Only Numbers")
        .min(0, "Must grater than 0 digit")
        .max(100, "Can't be longer than 100"),

    handling_fee: yup.number()
        .integer("Enter Only Numbers")
        .min(0, "Must grater than 0 digit")
        .max(100, "Can't be longer than 100"),

    attributes: yup.string(),

    product_name: yup.string().required("Enter Product Name")
        .min(2, "Must have at least 2 characters")
        .max(40, "Can't be longer than 40 characters"),

    product_brand_name: yup.string()
        .min(2, "Must have at least 2 characters")
        .max(40, "Can't be longer than 40 characters"),

    product_price: yup.number().required('Enter Product Price')
        .integer("Enter Only Numbers")
        .positive('Enter Between 1-1000000')
        .max(1000000, 'Enter Between 1-1000000'),

    product_size: yup.string(),

    product_colors: yup.string(),

    product_category: yup.string(),

    product_in_stock: yup.number().required("Enter Products in Stock")
        .integer("Enter Only Numbers")
        .min(1, "Must grater than 1 digit")
        .max(1000000, "Can't be longer than 1000000"),

    product_warranty: yup.number().integer("Enter Only Numbers")
        .min(0, 'Enter Between 0-200')
        .max(200, 'Enter Between 0-200'),

    warranty_type: yup.string(),

    product_discount: yup.number().integer("Enter Only Numbers")
        .min(0, 'Enter Between 0-100')
        .max(100, 'Enter Between 0-100'),

    sku: yup.string()
        .min(0, 'Enter Between 0-100')
        .max(100, 'Enter Between 0-100'),
    product_description: yup.string()
        .min(5, "Must have at least 5 characters")
        .max(50, "Can't be longer than 20 characters"),

    shipping_charges: yup.number().integer("Enter Only Numbers")
        .min(0, 'Enter Between 0-100')
        .max(100, 'Enter Between 0-100'),

    product_image_link: yup.string(),
});


class AddNew extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: false,
            showToast: false,
            categoryList: product_category_options,
            productCategory: '',
            size: [],
            color: [],

            productAttribute: [],
            productAttributesArray: [],

            warrantyType: 'Year',
            inputValue: '',
            image_link: [],

            categoryError: 'no_error',
            categoryErrorDiv: 'BorderDiv',
            image_linkError: 'no_error',
            image_linkErrorDiv: 'BorderDiv',
            productType: false,

            // Custom Fields
            customArray: [],
            fieldName: '',
            fieldValue: '',
            customFieldError: '',
        };
        this.handleProductValueChange = this.handleProductValueChange.bind(this);
        this.addCustomFieldBtnClicked = this.addCustomFieldBtnClicked.bind(this);
    }

    handleAddProductAttributeBtnClick() {
        // this{ fieldName: '', fieldValue: '' }
    }

    // Product Size
    handleProductSizeChange = (arr, actionMeta) => {
        this.setState({ size: arr });
    };

    // Product Color
    handleProductColorChange = (arr, actionMeta) => {
        this.setState({ color: arr });
        // this.setState({ color: newValue, colorError: 'no_error', colorErrorDiv: 'BorderDiv' });
    };

    // Product Attributes
    handleProductAttributesChange = (arr) => {
        this.setState({ productAttribute: arr });
    }


    // Product Category
    handleProductCategoryChange = (arr) => {
        this.setState({ productCategory: arr });
    }

    // Product Image Link
    handleImage_linkChange = (arr, actionMeta) => {
        this.setState({ image_link: arr, image_linkError: 'no_error', image_linkErrorDiv: 'BorderDiv' });
    };
    handleImageLinkInputChange = (aa) => {
        this.setState({ inputValue: aa });
    };
    handleImage_linkKeyDown = (event) => {
        const inputValue = this.state.inputValue;
        const image_link = this.state.image_link;
        if (!inputValue) return;
        switch (event.key) {
            case 'Enter':
            case 'Tab':
                this.setState({
                    inputValue: '',
                    image_link: [...image_link, createOption(inputValue)],
                });
                event.preventDefault();
        }
    };

    // Getting Product Categories from DB
    async componentDidMount() {
        const url = MuhalikConfig.PATH + '/api/products-categories/get-all';
        try {
            const response = await axios.get(url);
            this.setState({ categoryList: response.data.data });
        } catch (error) {
            console.error(error);
        }
    }
    //  Submit data to api
    async uploadData(data, currentComponent) {
        const url = MuhalikConfig.PATH + '/api/products/add';
        await axios.post(url, {
            data
        }, {
            headers: { 'authorization': await getUncodededTokenFromStorage() }
        }).then(function (response) {
            currentComponent.setState({ isLoading: false });
            currentComponent.setState({ showToast: true });
            return true;
        }).catch(function (error) {
            currentComponent.setState({ isLoading: false });
            alert('Error: ', error.response.data.message);
            return false;
        });
    }

    handleProductValueChange(e) {
        this.setState({ productType: e.target.value });
    }



    // Custom Fields
    setFieldNameChangeHandler = (e) => {
        this.setState({ fieldName: e.target.value })
    }
    setFieldValueChangeHandler = (e) => {
        this.setState({ fieldValue: e.target.value })
    }
    addCustomFieldBtnClicked = () => {
        if (this.state.fieldName != '' && this.state.fieldValue != '') {
            this.setState({ customFieldError: '' })
            const copyArray = Object.assign([], this.state.customArray);
            copyArray.push({
                fieldName: this.state.fieldName,
                fieldValue: this.state.fieldValue,
            })
            this.setState({ fieldName: '' })
            this.setState({ fieldValue: '' })
            this.setState({ customArray: copyArray })
        } else {
            this.setState({ customFieldError: 'Enter Field Name and Value' });
        }
    }
    deleteEvent = (index) => {
        const copyArray = Object.assign([], this.state.customArray);
        copyArray.splice(index, 1);
        this.setState({ customArray: copyArray });
    }


    render() {
        return (
            <Formik
                validationSchema={schema}
                initialValues={{
                    // product_type: 'Simple Product',
                    product_name: '', product_brand_name: '', product_price: '',
                    product_size: '', product_colors: '', product_category: '',
                    product_in_stock: 1, product_warranty: 0, warranty_type: 'Year', product_discount: 0,
                    sku: 0,
                    dimension_height: '', dimension_length: '', dimension_width: '', handling_fee: '',
                    product_description: '', shipping_charges: 40,
                    attributes: '',
                    product_image_link: '',
                }}
                onSubmit={(values, { setSubmitting, resetForm }) => {
                    if (this.state.image_link == '') {
                        // if (this.state.customFieldNameArray == '') {
                        //     this.setState({ categoryError: "error", categoryErrorDiv: 'RedBorderDiv' });
                        // }
                        if (this.state.image_link == '') {
                            this.setState({ image_linkError: "error", image_linkErrorDiv: 'RedBorderDiv' });
                        }
                        setSubmitting(false);
                    } else {
                        resetForm();
                        setSubmitting(true);
                        this.setState({ isLoading: true });
                        setTimeout(() => {
                            values.product_size = this.state.size;
                            values.product_colors = this.state.color;
                            values.attributes = this.state.customFieldNameArray;
                            values.product_image_link = this.state.image_link;
                            if (this.uploadData(values, this)) {
                                this.setState({ size: [], color: [], customFieldNameArray: [], image_link: [], inputValue: '' });
                                resetForm();
                            }
                            setSubmitting(false);
                        }, 500);
                    }
                }}
            >
                {
                    ({
                        handleSubmit, handleChange, values, touched, isValid, errors, handleBlur, isSubmitting
                    }) => (
                            <div>
                                <Form noValidate onSubmit={handleSubmit}>
                                    {this.state.showToast ? <ShowToast onCloseHandler={(e) => this.setState({ showToast: false })} show={this.state.showToast}
                                        message={'Product Uploaded Successfully'} icon={faThumbsUp} /> : null}
                                    <Row style={styles.title_row} noGutters>
                                        <FontAwesomeIcon icon={faPlus} style={styles.title_fontawesome} />
                                        <div className="mr-auto" style={styles.title}> Add New </div>
                                    </Row>
                                    <Form.Row style={styles.row}>
                                        <Form.Group as={Col} lg={8} md={8} sm={8} xs={12}>
                                            <Form.Label style={styles.label}>Product Name<span>*</span></Form.Label>
                                            <InputGroup>
                                                <Form.Control
                                                    type="text"
                                                    placeholder="Enter Product Name"
                                                    name="product_name"
                                                    value={values.product_name}
                                                    onChange={handleChange}
                                                    isInvalid={touched.product_name && errors.product_name}
                                                />
                                                <Form.Control.Feedback type="invalid">
                                                    {errors.product_name}
                                                </Form.Control.Feedback>
                                            </InputGroup>
                                        </Form.Group>
                                        <Form.Group as={Col} lg={4} md={4} sm={4} xs={12}>
                                            <Form.Label style={styles.label}>Product Category<span>*</span></Form.Label>
                                            {/* <InputGroup> */}
                                            <Select
                                                closeMenuOnSelect={true}
                                                isMulti
                                                onChange={this.handleProductCategoryChange}
                                                value={this.state.productCategory}
                                                placeholder="Select Category"
                                                options={this.state.categoryList}
                                            />
                                            {/* </InputGroup> */}
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row style={styles.row}>
                                        <Form.Group as={Col} lg={9} md={9} sm={12} xs={12}>
                                            <Card style={styles.card}>
                                                <Card.Header style={styles.card_header}>
                                                    Discruption
                                                </Card.Header>
                                                <Card.Body>
                                                    <Form.Group controlId="exampleForm.ControlTextarea1">
                                                        <Form.Control as="textarea" rows="8"
                                                            placeholder="Enter description about season, style, material etc" />
                                                    </Form.Group>
                                                </Card.Body>
                                            </Card>
                                        </Form.Group>
                                        <Form.Group as={Col} lg={3} md={3} sm={12} xs={12}>
                                            <Card style={styles.card}>
                                                <Card.Header style={styles.card_header}>
                                                    Dangerous Goods
                                                </Card.Header>
                                                <Card.Body style={{ paddingBottom: '26px' }}>
                                                    <Form.Check inline label="Not Specified" type="checkbox" style={styles.label} />
                                                    <br></br>
                                                    <br></br>
                                                    <Form.Check inline label="Ceramic" type="checkbox" style={styles.label} />
                                                    <br></br>
                                                    <br></br>
                                                    <Form.Check inline label="Glass" type="checkbox" style={styles.label} />
                                                    <br></br>
                                                    <br></br>
                                                    <Form.Check inline label="Metal" type="checkbox" style={styles.label} />
                                                    <br></br>
                                                    <br></br>
                                                    <Form.Check inline label="Plastic" type="checkbox" style={styles.label} />
                                                </Card.Body>
                                            </Card>
                                        </Form.Group>
                                    </Form.Row>

                                    <Row style={styles.row}>
                                        <Card style={styles.card}>
                                            <Card.Header style={styles.card_header}>
                                                <Row noGutters style={{ display: 'flex', alignItems: 'center' }}>
                                                    <Col lg={3} md={3} sm={3} xs={4}>
                                                        Product Data
                                                    </Col>
                                                    <Col lg={3} md={3} sm={5} xs={8}>
                                                        <Form.Control
                                                            as="select"
                                                            aria-describedby="countary"
                                                            name="countary"
                                                            value={values.product_type}
                                                            onChange={handleChange, this.handleProductValueChange}
                                                        >
                                                            <option value={false}> Simple Product </option>
                                                            <option value={true}> Variable Product </option>
                                                        </Form.Control>
                                                    </Col>
                                                </Row>
                                            </Card.Header>
                                            <Card.Body style={{ padding: '0.5%', margin: '0px' }}>
                                                <Tab.Container id="left-tabs-example" defaultActiveKey="first">
                                                    <Row style={{ margin: '0px' }} noGutters>
                                                        <Col lg={2} md={2} sm={2} xs={2} style={{ background: `${GlobalStyleSheet.admin_primry_color}` }}>
                                                            <Nav variant="pills" className="flex-column" style={{ margin: '0px' }}>
                                                                <Nav.Item>
                                                                    <div className="nav_link">
                                                                        <Nav.Link eventKey="General" style={styles.nav_link}>
                                                                            <FontAwesomeIcon size="xs" icon={faSlidersH} style={styles.product_fontawesome} />
                                                                            <div> General </div>
                                                                        </Nav.Link>
                                                                    </div>
                                                                </Nav.Item>
                                                                <Nav.Item>
                                                                    <div className="nav_link">
                                                                        <Nav.Link eventKey="Inventory" style={styles.nav_link}>
                                                                            <FontAwesomeIcon size="xs" icon={faStoreAlt} style={styles.product_fontawesome} />
                                                                            <div> Inventory </div>
                                                                        </Nav.Link>
                                                                    </div>
                                                                </Nav.Item>
                                                                <Nav.Item>
                                                                    <div className="nav_link">
                                                                        <Nav.Link eventKey="Shipping" style={styles.nav_link}>
                                                                            <FontAwesomeIcon size="xs" icon={faTruck} style={styles.product_fontawesome} />
                                                                            <div> Shipping </div>
                                                                        </Nav.Link>
                                                                    </div>
                                                                </Nav.Item>
                                                                <Nav.Item>
                                                                    <div className="nav_link">
                                                                        <Nav.Link eventKey="Attributes" style={styles.nav_link}>
                                                                            <FontAwesomeIcon size="xs" icon={faListAlt} style={styles.product_fontawesome} />
                                                                            <div> Attributes </div>
                                                                        </Nav.Link>
                                                                    </div>
                                                                </Nav.Item>
                                                                <Nav.Item>
                                                                    <div className="nav_link">
                                                                        <Nav.Link eventKey="Variations" style={styles.nav_link}>
                                                                            <FontAwesomeIcon size="xs" icon={faListAlt} style={styles.product_fontawesome} />
                                                                            <div> Variations </div>
                                                                        </Nav.Link>
                                                                    </div>
                                                                </Nav.Item>
                                                                <Nav.Item>
                                                                    <div className="nav_link">
                                                                        <Nav.Link eventKey="Advanced" style={styles.nav_link}>
                                                                            <FontAwesomeIcon size="xs" icon={faTools} style={styles.product_fontawesome} />
                                                                            <div> Advanced </div>
                                                                        </Nav.Link>
                                                                    </div>
                                                                </Nav.Item>
                                                                <Nav.Item>
                                                                    <div className="nav_link">
                                                                        <Nav.Link eventKey="Commission" style={styles.nav_link}>
                                                                            <FontAwesomeIcon size="xs" icon={faDollarSign} style={styles.product_fontawesome} />
                                                                            <div> Commission </div>
                                                                        </Nav.Link>
                                                                    </div>
                                                                </Nav.Item>
                                                            </Nav>
                                                        </Col>
                                                        <Col lg={10} md={10} sm={10} xs={10} style={{ padding: '2%' }}>
                                                            <Tab.Content>
                                                                <Tab.Pane eventKey="General">
                                                                    <Form.Group as={Row} noGutters>
                                                                        <Form.Label style={styles.label}>Brand Name</Form.Label>
                                                                        <InputGroup>
                                                                            <Form.Control
                                                                                type="text"
                                                                                placeholder="Enter Brand Name"
                                                                                name="product_brand_name"
                                                                                value={values.product_brand_name}
                                                                                onChange={handleChange}
                                                                                isInvalid={touched.product_brand_name && errors.product_brand_name}
                                                                            />
                                                                            <Form.Control.Feedback type="invalid">
                                                                                {errors.product_brand_name}
                                                                            </Form.Control.Feedback>
                                                                        </InputGroup>
                                                                    </Form.Group>
                                                                    <hr></hr>
                                                                    {this.state.productType ? null :
                                                                        <>
                                                                            <Form.Row>
                                                                                <Form.Group as={Col} lg={4} md={4} sm={12} xs={12}>
                                                                                    <Form.Label style={styles.label}>Product Price<span>*</span></Form.Label>
                                                                                    <InputGroup>
                                                                                        <Form.Control
                                                                                            type="number"
                                                                                            placeholder="Enter Product Price"
                                                                                            name="product_price"
                                                                                            value={values.product_price}
                                                                                            onChange={handleChange}
                                                                                            isInvalid={touched.product_price && errors.product_price}
                                                                                        />
                                                                                        <Form.Control.Feedback type="invalid">
                                                                                            {errors.product_price}
                                                                                        </Form.Control.Feedback>
                                                                                    </InputGroup>
                                                                                </Form.Group>
                                                                                <Form.Group as={Col} lg={4} md={4} sm={12} xs={12}>
                                                                                    <Form.Label style={styles.label}>Product in Stock</Form.Label>
                                                                                    <Form.Control type="number"
                                                                                        name="product_in_stock"
                                                                                        value={values.product_in_stock}
                                                                                        onChange={handleChange}
                                                                                        isInvalid={touched.product_in_stock && errors.product_in_stock}
                                                                                    />
                                                                                    <Form.Control.Feedback type="invalid">
                                                                                        {errors.product_in_stock}
                                                                                    </Form.Control.Feedback>
                                                                                </Form.Group>
                                                                                {/* <Form.Group as={Col} lg={4} md={4} sm={12} xs={12}>
                                                                                    <Form.Label style={styles.label}>Product Size</Form.Label>
                                                                                    <CreatableSelect
                                                                                        onChange={this.handleProductSizeChange}
                                                                                        options={product_size_options}
                                                                                        value={this.state.size}
                                                                                        placeholder="Select/Enter Size"
                                                                                    />
                                                                                </Form.Group> */}
                                                                                <Form.Group as={Col} lg={4} md={4} sm={12} xs={12}>
                                                                                    <Form.Label style={styles.label}>Product Color</Form.Label>
                                                                                    <CreatableSelect
                                                                                        onChange={this.handleProductColorChange}
                                                                                        options={product_color_options}
                                                                                        value={this.state.color}
                                                                                        placeholder="Select/Enter Color"
                                                                                    />
                                                                                </Form.Group>
                                                                            </Form.Row>
                                                                            <Form.Row>
                                                                                <Form.Group as={Col} lg={4} md={4} sm={12} xs={12}>
                                                                                    <Form.Label style={styles.label}>Product Warranty (months) </Form.Label>
                                                                                    <InputGroup>
                                                                                        <Form.Control
                                                                                            type="number"
                                                                                            placeholder="Enter Product Warranty"
                                                                                            name="product_warranty"
                                                                                            value={values.product_warranty}
                                                                                            onChange={handleChange}
                                                                                            isInvalid={touched.product_warranty && errors.product_warranty}
                                                                                        />
                                                                                        <Form.Control.Feedback type="invalid">
                                                                                            {errors.product_warranty}
                                                                                        </Form.Control.Feedback>
                                                                                    </InputGroup>
                                                                                </Form.Group>
                                                                                <Form.Group as={Col} lg={4} md={4} sm={12} xs={12}>
                                                                                    <Form.Label style={styles.label}> Warranty Type </Form.Label>
                                                                                    <InputGroup>
                                                                                        <InputGroup.Prepend>
                                                                                            <Form.Control
                                                                                                as="select"
                                                                                                name="warranty_type"
                                                                                                value={values.warranty_type}
                                                                                                onChange={handleChange}
                                                                                                isInvalid={touched.warranty_type && errors.warranty_type}
                                                                                            >
                                                                                                <option>Waranty Type</option>
                                                                                                <option> No Warranty </option>
                                                                                                <option> Brand Warranty </option>
                                                                                                <option> Local Warranty </option>
                                                                                                <option> Local Seller Warranty </option>
                                                                                                <option> Non-Local Warranty </option>
                                                                                                <option> Internationsl Warranty </option>
                                                                                                <option> Internationsl Seller Warranty </option>
                                                                                                <option> International Manufacturer Warranty </option>

                                                                                            </Form.Control>
                                                                                        </InputGroup.Prepend>
                                                                                        <Form.Control.Feedback type="invalid">
                                                                                            {errors.product_warranty}
                                                                                        </Form.Control.Feedback>
                                                                                    </InputGroup>
                                                                                </Form.Group>
                                                                                <Form.Group as={Col} lg={4} md={4} sm={12} xs={12}>
                                                                                    <Form.Label style={styles.label}>Product Discount</Form.Label>
                                                                                    <InputGroup>
                                                                                        <Form.Control
                                                                                            type="number"
                                                                                            placeholder="Enter Discount on on Product(%)"
                                                                                            name="product_discount"
                                                                                            value={values.product_discount}
                                                                                            onChange={handleChange}
                                                                                            isInvalid={touched.product_discount && errors.product_discount}
                                                                                        />
                                                                                        <InputGroup.Prepend>
                                                                                            <Button variant='primary'>%</Button>
                                                                                        </InputGroup.Prepend>
                                                                                        <Form.Control.Feedback type="invalid">
                                                                                            {errors.product_discount}
                                                                                        </Form.Control.Feedback>
                                                                                    </InputGroup>
                                                                                </Form.Group>
                                                                            </Form.Row>
                                                                        </>
                                                                    }
                                                                </Tab.Pane>
                                                                <Tab.Pane eventKey="Inventory">
                                                                    <Form.Row>
                                                                        <Form.Group as={Col} lg={6} md={6} sm={12} xs={12}>
                                                                            <Form.Label style={styles.label}>Product SKU</Form.Label>
                                                                            <InputGroup>
                                                                                <Form.Control
                                                                                    type="text"
                                                                                    placeholder="Enter Discount on on Product(%)"
                                                                                    name="sku"
                                                                                    value={values.sku}
                                                                                    onChange={handleChange}
                                                                                    isInvalid={touched.sku && errors.sku}
                                                                                />
                                                                                <Form.Control.Feedback type="invalid">
                                                                                    {errors.sku}
                                                                                </Form.Control.Feedback>
                                                                            </InputGroup>
                                                                        </Form.Group>
                                                                    </Form.Row>
                                                                </Tab.Pane>
                                                                <Tab.Pane eventKey="Shipping">
                                                                    <Form.Group as={Row} noGutters>
                                                                        <Form.Label style={styles.label}>Weight (kg)</Form.Label>
                                                                        <Form.Control type="number"
                                                                            name="product_weight"
                                                                            value={values.product_weight}
                                                                            onChange={handleChange}
                                                                            isInvalid={touched.product_weight && errors.product_weight}
                                                                        />
                                                                        <Form.Control.Feedback type="invalid">
                                                                            {errors.product_weight}
                                                                        </Form.Control.Feedback>
                                                                    </Form.Group>
                                                                    <Form.Group as={Row} noGutters>
                                                                        <Form.Label style={styles.label}>Dimensions (cm)</Form.Label>
                                                                        <InputGroup>
                                                                            <InputGroup.Prepend>
                                                                                <Form.Control type="number"
                                                                                    name="length"
                                                                                    value={values.dimension_length}
                                                                                    onChange={handleChange}
                                                                                    placeholder="Length"
                                                                                    isInvalid={touched.dimension_length && errors.dimension_length}
                                                                                />
                                                                                <Form.Control.Feedback type="invalid">
                                                                                    {errors.dimension_length}
                                                                                </Form.Control.Feedback>
                                                                            </InputGroup.Prepend>
                                                                            <Form.Control type="number"
                                                                                name="width"
                                                                                value={values.dimension_width}
                                                                                placeholder="Width"
                                                                                onChange={handleChange}
                                                                                isInvalid={touched.dimension_width && errors.dimension_width}
                                                                            />
                                                                            <InputGroup.Prepend>
                                                                                <Form.Control.Feedback type="invalid">
                                                                                    {errors.dimension_width}
                                                                                </Form.Control.Feedback>
                                                                                <Form.Control type="number"
                                                                                    name="height"
                                                                                    value={values.dimension_height}
                                                                                    placeholder="Height"
                                                                                    onChange={handleChange}
                                                                                    isInvalid={touched.dimension_height && errors.dimension_height}
                                                                                />
                                                                                <Form.Control.Feedback type="invalid">
                                                                                    {errors.dimension_height}
                                                                                </Form.Control.Feedback>
                                                                            </InputGroup.Prepend>
                                                                        </InputGroup>
                                                                    </Form.Group>
                                                                    <Form.Row>
                                                                        <Form.Group as={Col} lg={6} md={6} sm={12} xs={12}>
                                                                            <Form.Label style={styles.label}>Product Shipping Charges</Form.Label>
                                                                            <InputGroup>
                                                                                <Form.Control
                                                                                    type="text"
                                                                                    placeholder="Enter Product Shipping Charges"
                                                                                    name="shipping_charges"
                                                                                    value={values.shipping_charges}
                                                                                    onChange={handleChange}
                                                                                    isInvalid={touched.shipping_charges && errors.shipping_charges}
                                                                                />
                                                                                <InputGroup.Prepend>
                                                                                    <Button variant='primary'>Riyal</Button>
                                                                                </InputGroup.Prepend>
                                                                                <Form.Control.Feedback type="invalid">
                                                                                    {errors.shipping_charges}
                                                                                </Form.Control.Feedback>
                                                                            </InputGroup>
                                                                        </Form.Group>
                                                                        <Form.Group as={Col} lg={6} md={6} sm={12} xs={12}>
                                                                            <Form.Label style={styles.label}>Product Handling Fee</Form.Label>
                                                                            <InputGroup>
                                                                                <Form.Control
                                                                                    type="text"
                                                                                    placeholder="Enter Product Handling Fee"
                                                                                    name="handling_fee"
                                                                                    value={values.handling_fee}
                                                                                    onChange={handleChange}
                                                                                    isInvalid={touched.handling_fee && errors.handling_fee}
                                                                                />
                                                                                <InputGroup.Prepend>
                                                                                    <Button variant='primary'>Riyal</Button>
                                                                                </InputGroup.Prepend>
                                                                                <Form.Control.Feedback type="invalid">
                                                                                    {errors.handling_fee}
                                                                                </Form.Control.Feedback>
                                                                            </InputGroup>
                                                                        </Form.Group>
                                                                    </Form.Row>
                                                                </Tab.Pane>












                                                                <Tab.Pane eventKey="Attributes">
                                                                    <Form.Row>
                                                                        <Form.Group as={Col}>
                                                                            <Select
                                                                                closeMenuOnSelect={true}
                                                                                onChange={this.handleProductAttributesChange}
                                                                                value={this.state.productAttribute}
                                                                                placeholder="Select Attribute"
                                                                                options={product_attributes_list}
                                                                            />
                                                                        </Form.Group>
                                                                        <Form.Group as={Col} lg={2} md={2} sm={3} xs={12}>
                                                                            <Button onClick={this.handleAddProductAttributeBtnClick}>Add</Button>
                                                                        </Form.Group>
                                                                    </Form.Row>
                                                                    <hr></hr>
                                                                    {this.state.productAttributesArray.map((field, index) => {
                                                                        <ProductAttributeList
                                                                            name={field.fieldName}
                                                                            value={field.fieldValue}
                                                                            options={product_attributes_list}
                                                                        />
                                                                    })
                                                                    }
                                                                </Tab.Pane>
                                                                <Tab.Pane eventKey="Variations">
                                                                    {}
                                                                </Tab.Pane>
                                                                <Tab.Pane eventKey="Advanced">
                                                                    Advanced
                                                                </Tab.Pane>
                                                                <Tab.Pane eventKey="Commission">
                                                                    Commission
                                                                </Tab.Pane>
                                                            </Tab.Content>
                                                        </Col>
                                                    </Row>
                                                </Tab.Container>
                                            </Card.Body>
                                        </Card>
                                    </Row>
                                    <CustomFields
                                        customArray={this.state.customArray}
                                        name={this.state.fieldName}
                                        value={this.state.fieldValue}
                                        fieldNameHandler={this.setFieldNameChangeHandler.bind(this)}
                                        fieldValueHandler={this.setFieldValueChangeHandler.bind(this)}
                                        addFieldHandler={this.addCustomFieldBtnClicked.bind(this)}
                                        delete={this.deleteEvent.bind(this)}
                                    />
                                </Form>
                                <style jsx>
                                    {`
                                        .no_error {
                                            display: none;
                                        }
                                        .error{
                                            margin-top: 4px;  
                                            color: #DC3545;
                                            font-size: 14px;
                                            display: block;
                                        }
                                        .RedBorderDiv{
                                            border: 0.5px solid #DC3545;
                                        }
                                        .BorderDiv{
                                            border: none;
                                        }
                                        span {
                                            color: red;
                                        }
                                        .nav_link {
                                            background: ${GlobalStyleSheet.admin_primry_color};
                                            border-top: 0.5px solid #434556;
                                            border-bottom: 0.5px solid #434556;
                                            margin: 1.5px 0px;
                                            border-radius: 4px
                                        }
                                        p {
                                            text-align: center; 
                                            margin: 0px;
                                        }
                                    `}
                                </style>
                            </div>
                        )
                }
            </Formik>
        );
    }
}

const styles = {
    title_row: {
        borderBottom: '1px solid gray',
        padding: '1.5% 4%'
    },
    title_fontawesome: {
        color: 'gray',
        marginRight: '3%',
        width: '26px',
        height: '26px',
        maxHeight: '26px',
        maxWidth: '26px',
    },
    title: {
        color: 'gray'
    },

    nav_link: {
        color: 'white',
        fontSize: '13px',
        display: 'flex',
        alignItems: 'center',
        height: '45px',
    },


    row: {
        margin: '2%',
        padding: '0%'
    },
    card: {
        width: '100%',
    },
    card_header: {
        alignItems: 'center',
        // color: '#6A7074',
        fontSize: '15px',
        background: 'lightgray'
    },
    buttons: {
        background: `${GlobalStyleSheet.primry_color}`,
        border: 'none',
        fontSize: '10px',
    },
    submit_btn: {
        background: `${GlobalStyleSheet.primry_color}`,
        marginTop: '3%',
    },
    label: {
        fontSize: `${GlobalStyleSheet.form_label_fontsize}`,
    },
    term_condition_label: {
        width: '100%',
        fontSize: `${GlobalStyleSheet.form_label_fontsize}`,
        paddingTop: '-10px',
        marginTop: '-10px',
    },
    fontawesome: {
        color: `${GlobalStyleSheet.primary_text_color}`,
        width: '15px',
        height: '15px',
        maxHeight: '15px',
        maxWidth: '15px',
    },
    product_fontawesome: {
        color: `${GlobalStyleSheet.primary_text_color}`,
        marginRight: '10%',
        width: '17px',
        height: '17px',
        maxHeight: '17px',
        maxWidth: '17px',
    },
}
export default AddNew;
