import Router from 'next/router'
import { getTokenFromStorage } from './authentication-service';

 const  Guards = ctx => {
    const token = getTokenFromStorage();
    if(token.role !== 'admin'){
        Router.push('/index');
    }else {
        return;
    }
}

export default Guards;
