import { Row, Col, Image, Modal, Button, Form, InputGroup } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus, faArrowLeft, faTimes, faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft } from '@fortawesome/free-regular-svg-icons';
import GlobalStyleSheet from '../../../../../../../styleSheet';
import React, { Component } from 'react';
// https://drive.google.com/uc?export=view&id=1NXjE17yYJj2Pt7be9_J0DXdsnNzO4Yd4
// https://drive.google.com/uc?export=view&id=1F7ylPEh_UmvimCgPAOM7rf6gJ_yG8TK9


const ViewProduct = (props) => {
    const [imgPreview, setImgPreview] = React.useState(false);
    const [isSimpleProduct, setIsSimpleProduct] = React.useState(false);
    const [index, setIndex] = React.useState('')
    console.log('1111111:', props.data)

    // if (props.data.product_type == 'simple-product') {
    //     setIsSimpleProduct(true);
    // } else {
    //     setIsSimpleProduct(false);
    // }
    const len = props.data.product_image_link.length;

    function prevImage() {
        if (index > 0) {
            setIndex(index - 1)
        }
    }

    function nextImage() {
        if (index < (len - 1)) {
            setIndex(index + 1)
        }
    }

    return (
        <>
            <Row style={styles.title_row} noGutters>
                <FontAwesomeIcon className="fontawesome" icon={faArrowLeft} style={styles.title_fontawesome} onClick={props.back} className="mr-auto" />
                <div className="mr-auto" style={styles.title}> {props.data.product_name}</div>
                <div style={styles.title}> Prev </div>
                <div style={styles.title}> Next </div>
            </Row>
            {isSimpleProduct ?
                <Row style={styles.img_row} noGutters>
                    {props.data.product_image_link && props.data.product_image_link.map((element, index) =>
                        <div style={{ margin: '0%' }}>
                            <Image thumbnail fluid style={{ minWidth: '200px', maxWidth: '200px' }}
                                src={element.value} alt="Flowers in Chania" onClick={() => { setImgPreview(true), setIndex(index) }} />
                        </div>
                    )}
                </Row>
                :
                <>
                    <Row style={styles.img_row} noGutters>
                        <label><span>General Info</span></label>
                        <Row noGutters style={{ background: 'white', width: '100%', padding: '1%' }}>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Form.Label style={styles.label}>Product name:</Form.Label>
                                <InputGroup style={styles.fuck}>
                                    <Form.Label style={styles.label}>
                                        {props.data.product_name}
                                    </Form.Label>
                                </InputGroup>
                            </Form.Group>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Brand Name: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> SKU: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>




                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Warranty: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Warranty Type: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Discount: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>





                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Weight: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Length: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Width: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>
                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Height: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>

                            <Form.Group as={Col} lg={4} md={4} sm={6} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Purchase Notes: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>



                            <Form.Group as={Col} lg={12} md={12} sm={12} xs={12}>
                                <Row style={styles.fuck}>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}> Discruption: </Form.Label>
                                    </Col>
                                    <Col lg='auto' md='auto' sm='auto' xs='auto'>
                                        <Form.Label style={styles.label}>
                                            {props.data.product_name}
                                        </Form.Label>
                                    </Col>
                                </Row>
                            </Form.Group>
                        </Row>
                    </Row>
                </>
            }
            {/* Image Preview */}
            {
                imgPreview ?
                    <div className="modal-overlay">
                        <div className="modal-body">
                            <div className="close-modal">
                                <div className="mr-auto"></div>
                                <div className="mr-auto"></div>
                                <FontAwesomeIcon className="mr-auto" icon={faChevronLeft} style={styles.img_preview_fontawesome}
                                    onClick={() => prevImage} />
                                <FontAwesomeIcon className="mr-auto" icon={faChevronRight} style={styles.img_preview_fontawesome}
                                    onClick={nextImage} />
                                <div className="mr-auto"></div>
                                <FontAwesomeIcon icon={faTimes} style={styles.img_preview_fontawesome}
                                    onClick={() => setImgPreview(false)} />
                            </div>
                            <div className="image-container">
                                <img
                                    src={props.data.product_image_link[index].value}
                                    style={{ maxWidth: '100%', maxHeight: '90vh', margin: 'auto' }}
                                />
                            </div>
                        </div>
                    </div>
                    : null
            }
            <style jsx>
                {`
                    label {
                        width: 100%; 
                        text-align: center; 
                        border-bottom: 1px solid gray; 
                        line-height: 0.1em;
                        font-size: ${GlobalStyleSheet.form_label_fontsize};
                        margin: 20px 0%; 
                    } 
                    label span { 
                            background: ${GlobalStyleSheet.admin_primry_color};
                            color: white;
                            padding: 5px 10px;
                    }
                   .modal-overlay {
                        position: fixed;
                        top: 0;
                        left: 0;
                        bottom: 0;
                        right: 0;
                        z-index: 10;
                        width: 100%;
                        height: 100%;
                        background: rgba(21, 21, 21, 0.75);
                    }
                     .modal-body {
                        position: relative;
                        z-index: 11;
                        margin: 2.5%;
                        overflow: hidden;
                        max-width: 100%;
                        max-height: 100%;
                    }
                    .close-modal {
                        position: fixed;
                        display: flex;
                        top: 10px;
                        left: 0;
                        right: 0;
                        width: 100%;
                    }
                    .image-container {
                        display: grid;
                        height: 100%;
                        // display: flex;
                        // justify-content: center;
                        // align-items: center;
                    }
                `}
            </style>
        </>
    )
}

const styles = {
    title_row: {
        borderBottom: '1px solid gray',
        padding: '0.7% 4%',
        display: 'flex',
        alignItems: 'center'
    },
    title_fontawesome: {
        cursor: 'pointer',
        marginRight: '3%',
        width: '20px',
        height: '20px',
        maxHeight: '20px',
        maxWidth: '20px',
    },
    title: {
        color: `${GlobalStyleSheet.admin_primry_color}`,
    },
    label: {
        fontSize: `${GlobalStyleSheet.form_label_fontsize}`,
    },
    img_row: {
        margin: '2%',
        display: 'flex',
        justifyContent: 'center',
        zIndex: '999'
    },
    img_preview_fontawesome: {
        cursor: 'pointer',
        color: 'white',
        fontWeight: 'lighter',
        width: '30px',
        height: '30px',
        maxHeight: '30px',
        maxWidth: '30px',
    },
    general_info_label: {
        fontSize: `${GlobalStyleSheet.form_label_fontsize}`,
        width: '100%',
        borderBottom: '1px solid gray'
    },
    fuck: {
        border: `1px solid ${GlobalStyleSheet.body_color}`,
        // margin: '1%',
        // padding: 'px'
    }
}
export default ViewProduct;
