import React, { Component } from 'react';
import { Accordion, Form, InputGroup, Col, Button, Row, Card, Modal, Nav } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSlidersH } from '@fortawesome/free-solid-svg-icons';
import GlobalStyleSheet from '../../../../../../../styleSheet';
import Select, { components } from 'react-select';
import { groupedOptions } from '../../../../../../../sdk/consts/fields-data';
import AddNewFieldNameModal from './add-new-field-name-model';
const groupStyles = {
    border: `1px solid ${GlobalStyleSheet.admin_primry_color}`,
    borderRadius: '5px',
    background: 'white',
    color: `${GlobalStyleSheet.admin_primry_color}`,
};

const Group = props => (
    <div style={groupStyles}>
        <components.Group {...props} />
    </div>
);


const CustomFields = props => {
    const [modalShow, setModalShow] = React.useState(false);
    const [fieldsArray, setFielsdArray] = React.useState([]);
    const [fieldName, setFieldName] = React.useState('');
    const [fieldValue, setFieldValue] = React.useState('');
    const [error, setError] = React.useState('');

    function handleAddCustomFieldBtnClick() {
        if (fieldName != '' && fieldValue != '') {
            const copyArray = Object.assign([], fieldsArray);

            // if (props.isVariableProduct == true) {
            //     copyArray.forEach(element => {
            //         element.customField.push({
            //             name: fieldName,
            //             value: fieldValue
            //         });
            //         console.log("element.customField: ", element.customField);
            //     });
            //     this.setState({ variationsArray: copyArray })
            // } else {
            fieldsArray.push({
                name: fieldName,
                value: this.state.customFieldValue,
            })
            setError('');
            setFieldName('');
            setFieldValue('');
        } else {
            setError('Enter Field Name and Value');
        }
    }
    function deleteCustomFieldsClick(index) {
        const copyArray = Object.assign([], this.state.customFieldsArray);
        copyArray.splice(index, 1);
        this.setState({ customFieldsArray: copyArray });
    }
    return (
        <>
            {props.showCustomFields ?
                <Form.Label style={styles.label}> Please First Create Variations </Form.Label>
                :
                <Form.Row style={{ margin: '0%', padding: '1.5%', background: `${GlobalStyleSheet.body_color}` }}>
                    <Form.Group as={Col} lg={5} md={5} sm={12} xs={12}>
                        <Form.Label style={styles.label}>Field Name</Form.Label>
                        <Select
                            styles={GlobalStyleSheet.react_select_styles}
                            options={groupedOptions}
                            components={{ Group }}
                            value={fieldName}
                            onChange={(e) => setFieldName(e)}
                        />
                        <Nav.Link style={{ padding: '0%', margin: '0%', fontSize: '14px' }} onClick={() => setModalShow(true)}>
                            Add New
                        </Nav.Link>
                        <AddNewFieldNameModal
                            show={modalShow}
                            onHide={() => setModalShow(false)}
                        />
                    </Form.Group>
                    <Form.Group as={Col} lg={7} md={7} sm={12} xs={12}>
                        <Form.Label style={styles.label}>Field Value </Form.Label>
                        <InputGroup>
                            <Form.Control
                                style={{ fontSize: '14px' }}
                                type="text"
                                placeholder="Enter Value"
                                name="sku"
                                value={fieldValue}
                                onChange={(e) => setFieldValue(e.target.value)}
                            />
                        </InputGroup>
                    </Form.Group>
                    <Form.Label style={styles.label}>
                        {props.isVariableProduct ?
                            <span>For Variable Products each Custom Field will be added to all variations</span>
                            : null
                        }
                        <span style={{ color: 'red' }}>
                            {props.error}</span>
                    </Form.Label>
                    <Button variant="outline-primary" size="sm" block
                        onClick={handleAddCustomFieldBtnClick}>Add Field</Button>
                </Form.Row>
            }


            {/* Map */}
            <div style={{ background: `${GlobalStyleSheet.body_color}`, marginTop: '1%' }}>
                {props.customFieldsArray && props.customFieldsArray.map((data, index) =>
                    <Form.Row style={{ padding: '1% 2%' }} key={index}>
                        <Col lg={5} md={5} sm={12} xs={12}>
                            <Form.Control
                                as="select"
                                size="sm"
                                value={data.name}
                                onChange={() => data.name}
                            >
                                <option>{data.name}</option>
                            </Form.Control>
                        </Col>
                        <Col lg={7} md={7} sm={12} xs={12}>
                            <InputGroup>
                                <Form.Control
                                    type="text"
                                    size="sm"
                                    placeholder="Enter Value"
                                    name="sku"
                                    value={data.value}
                                    onChange={() => data.value}
                                />
                                <Button variant="outline-danger" size="sm" style={{ marginLeft: '1%' }}
                                    onClick={() => deleteCustomFieldsClick(index)}> delete</Button>
                            </InputGroup>
                        </Col>
                    </Form.Row>
                )}
            </div>
        </>
    )
}

const styles = {
    row: {
        margin: '2%',
        padding: '0%'
    },
    card: {
        width: '100%'
    },
    card_header: {
        width: '100%',
        alignItems: 'center',
        // color: '#6A7074',
        fontSize: `${GlobalStyleSheet.card_header_fontsize}`,
        background: `${GlobalStyleSheet.card_header_background}`,
    },
    label: {
        fontSize: '13px',
    },
    accordin_fontawesome: {
        color: `${GlobalStyleSheet.admin_primry_color}`,
        marginRight: '10%',
        width: '15px',
        height: '15px',
        maxHeight: '15px',
        maxWidth: '15px',
    },
}
export default CustomFields;
