import { Toast, Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbsUp } from '@fortawesome/free-solid-svg-icons';

let cc = '';
const ShowToast = props => {
    cc = props.color;
    return (
        <div
            style={{
                position: 'absolute',
                top: '40%',
                right: '40%',
                zIndex: '100'
            }}
        >
            <Toast onClose={props.onCloseHandler} show={props.show} delay={10000} autohide style={{
                display: 'absolute',
                background: '#e6ffe6',
                width: '300px',
                border: '0.5px solid black'
            }}>
                <Toast.Header>
                    <FontAwesomeIcon icon={props.iconName} style={styles.fontawesome} className="mr-auto"></FontAwesomeIcon>
                    <span style={styles.span} className="mr-auto">{props.header}</span>
                </Toast.Header>
                <Toast.Body style={styles.toastBody}>
                    <p style={{ textAlign: 'center' }}>
                        <span className="mr-auto">{props.message}</span>
                    </p>
                </Toast.Body>
            </Toast>
            {/* <style jsx>{`
                span {
                    width: 100%
                }
            `}</style> */}
        </div>
    )
}

export default ShowToast;


const styles = {
    span: {
        marginLeft: '20px',
        fontSize: '18px',
        color: `${cc}`,
        fontWeight: 'bold'
    },
    toastBody: {
        fontSize: '16px',
        padding: '10px',
    },
    fontawesome: {
        color: `${cc}`,
        width: '35px',
        height: '35px',
        maxHeight: '35px',
        maxWidth: '35px',
    },
}
