import { Modal, Form, Button } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function AlertModal(props) {
    return (
        <Modal
            {...props}
            size="md"
            aria-labelledby="alert-modal"
            centered
        >

            <Modal.Header closeButton style={{ color: `${props.color}` }}>
                <Modal.Title id="alert-modal">
                    {props.header}
                </Modal.Title>
            </Modal.Header>
            <Modal.Body style={{ display: 'inline', display: 'flex', alignItems: 'center', textAlign: 'center' }}>
                <FontAwesomeIcon icon={props.iconName} style={{
                    color: `${props.color}`,
                    width: '35px',
                    height: '35px',
                    maxHeight: '35px',
                    maxWidth: '35px',
                }} ></FontAwesomeIcon>
                <Form.Label style={{ fontSize: '14px', padding: '0%', margin: '0%' }}>
                    {props.message}
                </Form.Label>
            </Modal.Body>
            <Modal.Footer>
                <Button size="sm" onClick={props.onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
}

export default AlertModal;
