import React, { Component } from 'react';
import Link from 'next/link';

import { Navbar, Container, Form, Col, Row, InputGroup, Button, Image } from 'react-bootstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash, faMobileAlt } from '@fortawesome/free-solid-svg-icons';

import { Formik } from 'formik';
import * as yup from 'yup';

import GlobalStyleSheet from '../styleSheet';

// RegEx for phone number validation
const phoneRegExp = /^(\+?\d{0,4})?\s?-?\s?(\(?\d{3}\)?)\s?-?\s?(\(?\d{3}\)?)\s?-?\s?(\(?\d{4}\)?)?$/

const schema = yup.object({
    mobile: yup.string().required("Enter Mobile Number")
        .matches(phoneRegExp, "Phone number is not valid"),

    password: yup.string().required("Enter Password")
        .min(8, "Password must have at least 8 characters")
        .max(20, "Password can't be longer than 20 characters"),
});

class Login extends Component {

    state = {
        hide: true
    };
    showPassword = ev => {
        this.setState({ hide: !this.state.hide })
    }

    render() {
        const { hide } = this.state;
        let eyeBtn;
        if (this.state.hide) {
            eyeBtn = <FontAwesomeIcon icon={faEye} style={styles.fontawesome} />;
        } else {
            eyeBtn = <FontAwesomeIcon icon={faEyeSlash} style={styles.fontawesome} />;
        }

        return (
            <Formik
                validationSchema={schema}
                // onSubmit={console.log}
                initialValues={{
                    mobile: '', password: ''
                }}
                onSubmit={(values, { setSubmitting, resetForm }) => {
                    // When button submits form and form is in the process of submitting, submit button is disabled
                    setSubmitting(true);
                    // Resets form after submission is complete
                    resetForm();
                    // Sets setSubmitting to false after form is reset
                    setSubmitting(false);

                    setTimeout(() => {
                        alert(JSON.stringify(values, null, 2));
                        resetForm();
                        setSubmitting(false);
                    }, 500);
                }}
            >
                {
                    ({
                        handleSubmit,
                        handleChange,
                        values,
                        touched,
                        isValid,
                        errors,
                        handleBlur,
                        isSubmitting
                    }) => (
                            <div style={styles.body}>
                                <Navbar variant="dark" style={{ background: `${GlobalStyleSheet.primry_color}` }}>
                                    <Navbar.Brand href="/" className="mr-auto" > Muhalik </Navbar.Brand>
                                </Navbar>

                                <Container>
                                    <Row>
                                        <Col lg={3} md={3} sm={1} xs={0}></Col>
                                        <Col style={styles.container}>
                                            <p>
                                                <Image src="/static/muhalik.jpg" roundedCircle thumbnail fluid style={{ width: '25%', maxWidth: '150px' }} />
                                            </p>
                                            <h6 className="text-center" style={{ width: '100%', paddingBottom: '10px' }}>Forgot Password</h6>
                                            <Form noValidate onSubmit={handleSubmit}>
                                                {console.log("fucking values fffffffffffffffff: ", values)}
                                                <Form.Row>
                                                    <Form.Group as={Col} controlId="validationMobile">
                                                        <Form.Label style={styles.label}>Enter Your Mobile Number</Form.Label>

                                                        <InputGroup>
                                                            <InputGroup.Prepend>
                                                                <Button id="eyeBtn" style={styles.fontawesome_btn}>
                                                                    <FontAwesomeIcon icon={faMobileAlt} style={styles.fontawesome} />
                                                                </Button>
                                                            </InputGroup.Prepend>
                                                            <Form.Control
                                                                type="text"
                                                                placeholder="+966590911891"
                                                                aria-describedby="mobile"
                                                                name="mobile"
                                                                value={values.mobile}
                                                                onChange={handleChange}
                                                                isInvalid={touched.mobile && errors.mobile}
                                                            />
                                                            <Form.Control.Feedback type="invalid">
                                                                {errors.mobile}
                                                            </Form.Control.Feedback>
                                                        </InputGroup>
                                                    </Form.Group>
                                                </Form.Row>

                                                {/* 4th Row */}
                                                <Form.Row>
                                                    <Button type="submit" onSubmit={handleSubmit} block style={styles.submit_btn}>Continue</Button>
                                                </Form.Row>
                                                {/* End 4th Row */}
                                                <Form.Row>
                                                    <Form.Label className="text-center" style={styles.term_condition_label}>
                                                        By creating an account, you agree to Muhalik's
                                                        <span>
                                                            <Link href="#"> Terms & Conditions </Link>
                                                        </span>
                                                        and
                                                        <span>
                                                            <Link href="#"> Privacy Statement </Link>
                                                        </span>
                                                    </Form.Label>
                                                </Form.Row>
                                            </Form>
                                        </Col>
                                        <Col lg={3} md={3} sm={1} xs={0}></Col>
                                    </Row>
                                </Container>
                                <style jsx>
                                    {`
                                        span {
                                            color: red;
                                        }
                                        p {
                                            text-align: center; 
                                            margin: 0px;
                                        }
                                    `}
                                </style>
                            </div>
                        )}
            </Formik>
        );
    }
}

const styles = {
    body: {
        background: `${GlobalStyleSheet.body_color}`,
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        bottom: '0',

    },
    buttons: {
        background: `${GlobalStyleSheet.primry_color}`,
        border: 'none',
        fontSize: '10px',
    },
    submit_btn: {
        background: `${GlobalStyleSheet.primry_color}`,
    },
    container: {
        background: 'white',
        // border: `0.5px solid ${GlobalStyleSheet.primry_color}`,
        padding: '20px 30px',
        margin: '5% 8%',
    },
    label: {
        width: '100%',
        fontSize: '14px',
    },
    term_condition_label: {
        width: '100%',
        fontSize: '13px',
        padding: '10px',
    },
    fontawesome_btn: {
        background: `${GlobalStyleSheet.primry_color}`,
        border: 'none',
    },
    fontawesome: {
        color: `${GlobalStyleSheet.primary_text_color}`,
        width: '15px',
        height: '15px',
        maxHeight: '15px',
        maxWidth: '15px',
    },
}

export default Login;
