import {
    Navbar, Nav, Form, InputGroup, FormControl, Image, Button,
    NavDropdown, DropdownButton, Card, Dropdown, ButtonGroup,
    Row, Col,
} from 'react-bootstrap';
import GlobalStyleSheet from '../../../../styleSheet';
import Link from 'next/link';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faUserPlus, faLanguage, faPowerOff, faUser, faCartPlus, faHandsHelping, faPen, faSignOutAlt, faGlobe, faLuggageCart } from '@fortawesome/free-solid-svg-icons';
import MuhalikConfig from '../../../../sdk/muhalik.config';
import axios from 'axios';
function Toolbar(props) {

    // let dashboard = null;
    // let login = null;

    // if (props.token == 'vendor') {
    //     dashboard = <Nav.Link href='./vendor' style={styles.nav_link}>Vendor dashboard</Nav.Link>
    //     login = <Nav.Link onClick={props.logout} style={styles.nav_link}>
    //         <FontAwesomeIcon icon={faPowerOff} style={styles.nav_fontawesome} />
    //         <div style={styles.link_text}>Logout</div>
    //     </Nav.Link>
    // } else if (props.token == 'admin') {
    //     dashboard = <>
    //         <Nav.Link href='./admin' style={styles.nav_link}> Admin dashboard </Nav.Link>
    //         <Nav.Link href='./vendor' style={styles.nav_link}>Vendor dashboard</Nav.Link>
    //     </>
    //     login = <Nav.Link onClick={props.logout} style={styles.nav_link}>
    //         <FontAwesomeIcon icon={faPowerOff} style={styles.nav_fontawesome} />
    //         <div style={styles.link_text}>Logout</div>
    //     </Nav.Link>
    // } else if (props.token == 'customer') {
    //     login = <Nav.Link onClick={props.logout} style={styles.nav_link}> Logout </Nav.Link>
    // } else {
    //     login = <>
    // <Nav.Link href='./login' style={styles.nav_link}>
    //     <FontAwesomeIcon icon={faUser} style={styles.nav_fontawesome} />
    //     <div style={styles.link_text}>Login/Signup</div>
    // </Nav.Link>
    //         {/* <Nav.Link href='./vendor-signup'> Register_as_Shop </Nav.Link> */}
    //     </>
    // }

    const [all, setAll] = React.useState('');

    return (
        <div>
            <Card>
                <Card.Body style={{ padding: '0%', margin: '0%' }}>
                    <Navbar>
                        <Row style={styles.row} >
                            <Col lg={3} md={12} sm={12} xs={12} style={styles.col}>
                                <div style={{ display: 'flex', alignItems: 'center' }}>
                                    <Image src="muhalik.jpg" roundedCircle thumbnail fluid style={{ width: '60px', position: 'absolute' }} />
                                    <h5 style={{ marginLeft: '70px' }} className="text-center">Muhalik</h5>
                                </div>
                            </Col>
                            <Col lg={6} md={12} sm={12} xs={12} style={styles.col}>
                                <InputGroup style={styles.search_div}>
                                    <InputGroup.Prepend >
                                        <Form.Control
                                            style={styles.search_critera}
                                            as="select"
                                            name="countary"
                                            value={all}
                                            onChange={e => setAll(e.target.value)}
                                        >
                                            <option>All</option>
                                            <option>Cloths</option>
                                            <option>Mobiles</option>
                                            <option>Laptops</option>
                                            <option>Electronics</option>
                                        </Form.Control>
                                    </InputGroup.Prepend>
                                    <FormControl style={styles.search_box} placeholder="Search here" />
                                    <InputGroup.Append style={styles.append}>
                                        <FontAwesomeIcon icon={faSearch} style={styles.search_fontawesome} />
                                    </InputGroup.Append>
                                </InputGroup>
                            </Col>
                            <div className='mr-auto'></div>
                            <Col lg={3} md={12} sm={12} xs={12} style={styles.col}>
                                <Nav style={styles.nav}>
                                    <Nav.Link href='./login' style={styles.nav_link}>
                                        <FontAwesomeIcon icon={faUser} style={styles.nav_fontawesome} />
                                        <FontAwesomeIcon icon={faUserPlus} style={styles.nav_fontawesome} />
                                        <div className='nav_link_text'>Signin/up</div>
                                    </Nav.Link>
                                    <div className='mr-auto'></div>
                                    <Nav.Link href='#' style={styles.nav_link}>
                                        <FontAwesomeIcon icon={faGlobe} style={styles.nav_fontawesome} />
                                        <div className='nav_link_text'>Eng</div>
                                    </Nav.Link>
                                    <div className='mr-auto'></div>
                                    <Nav.Link href="#" style={styles.nav_link}>
                                        <FontAwesomeIcon icon={faLuggageCart} style={styles.nav_cart_fontawesome} />
                                        <div className='nav_link_text'>Cart</div>
                                    </Nav.Link>
                                    {/* <NavDropdown title={
                                <Image src="muhalik.jpg" roundedCircle thumbnail fluid style={{ width: '35%', maxWidth: '200px' }} />
                            } id="nav-dropdown"  alignRight>
                                <NavDropdown.Item eventKey="4.1">Action</NavDropdown.Item>
                                <NavDropdown.Item eventKey="4.2">Another action</NavDropdown.Item>
                                <NavDropdown.Item eventKey="4.3">Something else here</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item eventKey="4.4">Separated link</NavDropdown.Item>
                            </NavDropdown> */}
                                </Nav>
                            </Col>
                        </Row>
                    </Navbar>
                </Card.Body>
            </Card>
            <style jsx>
                {`
                    .nav_link_text {
                        text-align: center;
                        white-space: nowrap;
                        overflow: hidden;
                        font-size: 13px;
                    }

                    .nav_link_text:hover {
                        color: ${GlobalStyleSheet.primry_color}
                    }

                `}
            </style>
        </div >
    )
}

Toolbar.getInitialProps = async ctx => {
    const url = MuhalikConfig.PATH + '/api/categories/categories';
    try {
        const response = await axios.get(url);
        // this.setState({ categoryList: response.data.data });
        return { searchCategory: response.data.data }
    } catch (error) {
        console.error(error);
        return null;
    }
    // const res = await fetch('')
    // const json = await res.json()

}

const styles = {
    row: {
        width: '100%',
        display: 'flex',
        alignItems: 'center'
    },
    col: {
        // marginTop: '10px'
    },
    nav: {
        display: 'flex',
        alignItems: 'center'
    },
    // form: {
    //     border: `2px solid ${GlobalStyleSheet.primry_color}`,
    //     width: '60%',
    //     borderRadius: '25px',
    //     padding: '0px 5px'
    // },
    // search_div: {
    //     margin: '0% 5% 0% 7%',
    //     // border: `1px solid ${GlobalStyleSheet.primry_color}`,
    // },
    search_critera: {
        // background: `${GlobalStyleSheet.primry_color}`,
        border: `2px solid ${GlobalStyleSheet.primry_color}`,
        color: 'black',
        padding: '0px 10px',
        display: 'flex',
        textAlign: 'center',
        alignItems: 'center',
        borderTopLeftRadius: '20px',
        borderBottomLeftRadius: '20px',
    },
    fontawesome: {
        color: `${GlobalStyleSheet.primry_color}`,
        width: '18px',
        height: '18px',
        maxHeight: '18px',
        maxWidth: '18x',
    },

    // search_box: {
    //     border: `2px solid ${GlobalStyleSheet.primry_color}`,
    // },

    append: {
        background: `${GlobalStyleSheet.primry_color}`,
        padding: '0px 25px',
        display: 'flex',
        alignItems: 'center',
        borderTopRightRadius: '20px',
        borderBottomRightRadius: '20px',
    },

    nav_link: {
        textAlign: 'center',
        whiteSpace: 'nowrap',
        overflow: 'hidden',
        fontSize: '14px',
        marginLeft: '5%'
    },

    search_fontawesome: {
        color: `${GlobalStyleSheet.primary_text_color}`,
        width: '18px',
        height: '18px',
        maxHeight: '18px',
        maxWidth: '18px',
    },
    nav_fontawesome: {
        color: `${GlobalStyleSheet.primry_color}`,
        width: '25px',
        height: '25px',
        maxHeight: '25px',
        maxWidth: '25px',
        marginBottom: '1px'
    },
    nav_cart_fontawesome: {
        color: `${GlobalStyleSheet.primry_color}`,
        width: '28px',
        height: '28px',
        maxHeight: '28px',
        maxWidth: '28px',
    },
    dropDown_fontawesome: {
        color: `${GlobalStyleSheet.admin_primry_color}`,
        margin: '0px 20px 0px 0px',
        width: '18px',
        height: '18px',
        maxHeight: '18px',
        maxWidth: '18px',
    }

}

export default Toolbar;
