import { Form, InputGroup, Col, Button } from 'react-bootstrap'

const CustomFields = props => {
    return (

        <Row style={styles.row}>
            <Card style={styles.card}>
                <Card.Header style={styles.card_header}>
                    Custome Fields
                                            </Card.Header>
                <Card.Body >
                    <div style={{ background: 'lightGray', margin: '0.5% 1%' }}>
                        {this.state.customArray.map((data, index) =>
                            <CustomFields
                                key={index}
                                name={data.fieldName}
                                value={data.fieldValue}
                                delete={this.deleteEvent.bind(this, index)}
                            />
                        )}
                    </div>
                    <Form.Row style={{ margin: '0.5% 1%', padding: '1% 2%', background: 'lightGray' }}>
                        <Form.Group as={Col} lg={5} md={5} sm={12} xs={12}>
                            <Form.Label style={styles.label}>Field Name</Form.Label>
                            <Form.Control
                                as="select"
                                value={this.state.fieldName}
                                onChange={this.setFieldNameChangeHandler.bind(this)}
                            >
                                <option>Select</option>
                                <option>Aaaa</option>
                                <option>Bbb</option>
                                <option>Ccc</option>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group as={Col} lg={7} md={7} sm={12} xs={12}>
                            <Form.Label style={styles.label}>Field Value </Form.Label>
                            <InputGroup>
                                <Form.Control
                                    type="text"
                                    placeholder="Enter Value"
                                    name="sku"
                                    value={this.state.fieldValue}
                                    onChange={this.setFieldValueChangeHandler.bind(this)}
                                />
                            </InputGroup>
                        </Form.Group>
                        <Form.Label style={styles.label}><span style={{ color: 'red' }}>{this.state.customFieldError}</span></Form.Label>
                        <Button variant="outline-primary" size="sm" block style={{ margin: '2% 0px' }}
                            onClick={this.addCustomFieldBtnClicked.bind(this)}>Add Field</Button>
                    </Form.Row>
                </Card.Body>
            </Card>
        </Row>

    )
}

const styles = {
    card_header: {
        alignItems: 'center',
        // color: '#6A7074',
        fontSize: '15px',
        background: 'lightgray'
    },
    label: {
        fontSize: '13px',
    }
}
export default CustomFields;
