import React, { Component } from 'react';
import { Row, Form, Col, Card } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import { faThumbsUp, faThumbsDown } from '@fortawesome/free-regular-svg-icons';
import ShowToast from '../../../../toast';

class AddCategory extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: false,
            showToast: false,
            categoryValue: '',
        }
    }
    //  Submit data to api
    async uploadProduct(data, currentComponent) {
        console.log('data: ', data)
        // const url = MuhalikConfig.PATH + '/api/products/add';
        // await axios.post(url, {
        //     data
        // }, {
        //     headers: { 'authorization': await getUncodededTokenFromStorage() }
        // }).then(function (response) {
        currentComponent.setState({ isLoading: false });
        currentComponent.setState({ showToast: true });
        return true;
        // }).catch(function (error) {
        //     currentComponent.setState({ isLoading: false });
        //     alert('Error: ', error.response.data.message);
        //     return false;
        // });
    }
    handleCategoryValueChange = (e) => {
        this.setState({ categoryValue: e.target.value })
    }

    render() {
        return (
            <>
                {this.state.showToast ? <ShowToast onCloseHandler={(e) => this.setState({ showToast: false })} show={this.state.showToast}
                    header={'Success'} message={'Category Added Successfully'} iconName={faThumbsUp} color={"green"} />
                    : null
                }
                <Row style={styles.title_row} noGutters>
                    <FontAwesomeIcon icon={faPlus} style={styles.title_fontawesome} />
                    <div className="mr-auto" style={styles.title}> Add New Category </div>
                </Row>
                <Form.Row>
                    <Card style={styles.card}>
                        <Card.Header>
                            Product Category
                        </Card.Header>
                        <Card.Body>
                            {/* <Form.Group as={Col} lg={3} md={3} sm={6} xs={6}> */}
                            <Form.Control
                                type="text"
                                size="sm"
                                placeholder="Enter Category Value"
                                name="sku"
                                value={this.state.categoryValue}
                                onChange={this.handleCategoryValueChange}
                            />
                            {/* </Form.Group> */}
                        </Card.Body>
                    </Card>
                </Form.Row>
            </>
        );
    }
}

const styles = {
    title_row: {
        borderBottom: '1px solid gray',
        padding: '1.5% 4%'
    },
    title_fontawesome: {
        color: 'gray',
        marginRight: '3%',
        width: '26px',
        height: '26px',
        maxHeight: '26px',
        maxWidth: '26px',
    },
    title: {
        color: 'gray'
    },

    card: {
        width: '100%',
        margin: '5%'
    }
}

export default AddCategory;
