import React, { Component } from 'react';
import Router from 'next/router'
import axios from 'axios'
import Dashboard from './components/admin/dashboard/dashboard';
import DashboardSideDrawer from './components/admin/dashboard/dashboard-side-drawer';
import AdminLayout from './components/admin/layout/AdminLayout';
import GlobalStyleSheet from '../styleSheet';
import MuhalikConfig from '../sdk/muhalik.config'
import { chectAuth, removeTokenFromStorage, getTokenFromStorage } from '../sdk/core/authentication-service';

const BackDrop = props => (
    <div>
        <div onClick={props.click}>
        </div>
        <style jsx>{`
             position: fixed;
             width: 100%;
             height: 100%;
             top: 0;
             left: 0;
             background: rgba(0, 0, 0, 0.3);
             z-index: 100;
        `}</style>
    </div>
)

let token = ''
class Admin extends Component {
    constructor(props) {
        super(props);
        this.authUser()
        this.state = {
            sideDrawerOpen: false,
            showWrapper: true,

            categories: [],
            subCategories: [],
            fields: [],
            jwt_token: '',
        }
    }

    async componentDidMount() {
        token = await getTokenFromStorage()

        function getCategories() {
            return axios.get(MuhalikConfig.PATH + '/api/categories/categories');
        }
        function getFields() {
            return axios.get(MuhalikConfig.PATH + '/api/categories/fields');
        }

        axios.all([getCategories(), getFields()])
            .then(axios.spread(function (response) {
                this.setState({ categories: response.data.category.docs, subCategories: response.data.sub_category.docs })
            })).catch(function (error) {
                console.log('ERRORR:', error);
            });
    }

    async authUser() {
        this.setState({ jwt_token: await chectAuth('admin') });
    }

    drawerToggleClickHandler = () => {
        this.setState(prevState => {
            return { sideDrawerOpen: !prevState.sideDrawerOpen };
        });
    };
    ShowWrapperClickHandler = () => {
        this.setState(prevState => {
            return { showWrapper: !prevState.showWrapper };
        });
    };

    backdropClickHandler = () => {
        this.setState({ sideDrawerOpen: false });
    };

    logout() {
        removeTokenFromStorage();
    }

    render() {
        let backdrop;
        if (this.state.sideDrawerOpen) {
            backdrop = <BackDrop click={this.backdropClickHandler} />;
        }

        return (
            <div style={styles.body}>
                {/* <AdminLayout> */}
                <Dashboard
                    categories={this.state.categories}
                    subCategories={this.state.subCategories}
                    fields={this.state.fields}
                    token={this.state.jwt_token}
                    show={this.state.showWrapper}
                    drawerClickHandler={this.drawerToggleClickHandler
                    }
                    wrapperBtnClickHandler={this.ShowWrapperClickHandler} logoutClickHandler={this.logout} />
                <DashboardSideDrawer token={this.state.jwt_token} show={this.state.sideDrawerOpen}
                    click={this.backdropClickHandler} logoutClickHandler={this.logout} />
                {backdrop}
                {/* </AdminLayout> */}
            </div>
        );
    }
}

const styles = {
    body: {
        background: `${GlobalStyleSheet.body_color}`,
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        minHeight: '100vh',
    },
}

export default Admin;
