import React, { Component } from 'react';
import { Dropdown, ButtonGroup, SplitButton, DropdownButton, FormControl, Form, Col, Row, Card, InputGroup, Button, Image } from 'react-bootstrap';

// The forwardRef is important!!
// Dropdown needs access to the DOM node in order to position the Menu
const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <a
    href=""
    ref={ref}
    onClick={e => {
      e.preventDefault();
      onClick(e);
    }}
  >
    {children}
    &#x25bc;
  </a>
));

// forwardRef again here!
// Dropdown needs access to the DOM of the Menu to measure it
const CustomMenu = React.forwardRef(
  ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
    const [value, setValue] = React.useState('');

    return (
      <div
        ref={ref}
        style={style}
        className={className}
        aria-labelledby={labeledBy}
      >
        <FormControl
          autoFocus
          className="mx-3 my-2 w-auto"
          placeholder="Type to filter..."
          onChange={e => setValue(e.target.value)}
          value={value}
        />
        <ul className="list-unstyled">
          {React.Children.toArray(children).filter(
            child =>
              !value || child.props.children.toLowerCase().startsWith(value),
          )}
        </ul>
      </div>
    );
  },
);

class Orders extends Component {
  state = {
    value: ''
  }

  Change
  render() {
    return (
      <div>
        <Dropdown>
          <Form.Label>Product Category </Form.Label>
          <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components" >Product Category</Dropdown.Toggle>
          <Dropdown.Menu as={CustomMenu} alignRight>
            <Dropdown.Item eventKey="1">Red</Dropdown.Item>
            <Dropdown.Item eventKey="2">Blue</Dropdown.Item>
            <Dropdown.Item eventKey="3">Orange</Dropdown.Item>
            <Dropdown.Item eventKey="4">Purple</Dropdown.Item>
            <Dropdown.Item eventKey="5">Fuck</Dropdown.Item>
            <Dropdown.Item eventKey="6">Shit</Dropdown.Item>
            <Dropdown.Item eventKey="7" >Mig</Dropdown.Item>
            <Dropdown.Item eventKey="8">Green</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        <input
          as="select"
          aria-describedby="countary"
          name="countary"
          onChange={this.Change}
        >
          <option>Select</option>
          <option> KSA </option>
          <option> Pak </option>
        </input>
      </div>
    );
  }
}

export default Orders;
