import { Row, Col, Image, Modal, Button, Container } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlus, faArrowLeft, faCross, faTimes } from '@fortawesome/free-solid-svg-icons';
import { faArrowAltCircleLeft } from '@fortawesome/free-regular-svg-icons';
import GlobalStyleSheet from '../../../../../../../styleSheet';
import React, { Component } from 'react';
// https://drive.google.com/uc?export=view&id=1NXjE17yYJj2Pt7be9_J0DXdsnNzO4Yd4
// https://drive.google.com/uc?export=view&id=1F7ylPEh_UmvimCgPAOM7rf6gJ_yG8TK9


const ViewProduct = props => {
    const [imgPreview, setImgPreview] = React.useState(false)
    const imgData = props.product_image_link;
    const [index, setIndex] = React.useState('')



    return (
        <>
            <Row style={styles.title_row} noGutters>
                <FontAwesomeIcon className="fontawesome" icon={faArrowLeft} style={styles.title_fontawesome} onClick={props.back} className="mr-auto" />
                <div className="mr-auto" style={styles.title}> {props.data.product_name}</div>
                <div style={styles.title}> Prev </div>
                <div style={styles.title}> Next </div>
            </Row>
            <Row style={styles.img_row} noGutters>
                {props.data.product_image_link && props.data.product_image_link.map((element, index) =>
                    <div style={{ margin: '0%' }}>
                        <Image thumbnail fluid style={{ minWidth: '200px', maxWidth: '200px' }}
                            src={element.value} alt="Flowers in Chania" onClick={() => { setImgPreview(true), setData(element), setIndex(index) }} />
                    </div>
                )}
            </Row>
            {/* Image Preview */}
            <div>
                {imgPreview ?
                    <div className="modal-overlay">
                        <div className="modal-body">
                            <div className="close-modal">
                                <FontAwesomeIcon className="fontawesome" icon={faTimes} style={styles.close_img_preview_fontawesome}
                                    onClick={() => setImgPreview(false)} />
                            </div>
                            <div className="image-container">
                                <img
                                    src={data.value}
                                    style={{ maxWidth: '100%', maxHeight: '90vh', margin: 'auto' }}
                                />
                            </div>
                        </div>
                    </div>
                    : null
                }
            </div>
            <style jsx>
                {`
                    .fontawesome: hover{
                        background: red;
                        color: blue;
                    }
                   .modal-overlay {
                        position: fixed;
                        top: 0;
                        left: 0;
                        bottom: 0;
                        right: 0;
                        z-index: 10;
                        width: 100%;
                        height: 100%;
                        background: rgba(21, 21, 21, 0.75);
                    }
                     .modal-body {
                        position: relative;
                        z-index: 11;
                        margin: 2%;
                        overflow: hidden;
                        max-width: 100%;
                        max-height: 100%;
                    }
                    .close-modal {
                        position: fixed;
                        top: 10px;
                        right: 10px;
                    }
                    .image-container {
                        display: grid;
                        height: 100%;
                        // display: flex;
                        // justify-content: center;
                        // align-items: center;
                    }
                `}
            </style>
        </>
    )
}

const styles = {
    title_row: {
        borderBottom: '1px solid gray',
        padding: '0.7% 4%',
        display: 'flex',
        alignItems: 'center'
    },
    title_fontawesome: {
        cursor: 'pointer',
        marginRight: '3%',
        width: '20px',
        height: '20px',
        maxHeight: '20px',
        maxWidth: '20px',
    },
    title: {
        color: `${GlobalStyleSheet.admin_primry_color}`,
    },
    img_row: {
        margin: '5%',
        display: 'flex',
        justifyContent: 'center',
        zIndex: '999'
    },
    close_img_preview_fontawesome: {
        cursor: 'pointer',
        color: 'white',
        width: '30px',
        height: '30px',
        maxHeight: '30px',
        maxWidth: '30px',
    },
}
export default ViewProduct;
