import { Form, InputGroup, Col, Button, Row, Card } from 'react-bootstrap'

const CustomFields = props => {
    return (

        <Card style={styles.card}>
            <Card.Header style={styles.card_header}>
                Custome Fields
                </Card.Header>
            <Card.Body >
                <div style={{ background: 'lightGray', margin: '0.5% 1%' }}>
                    {props.customArray.map((data, index) =>
                        // <CustomFields
                        //     key={index}
                        //     name={data.fieldName}
                        //     value={data.fieldValue}
                        //     delete={this.deleteEvent.bind(this, index)}
                        // />
                        <Form.Row style={{ padding: '1% 2%' }} key={index}>
                            <Form.Group as={Col} lg={5} md={5} sm={12} xs={12}>
                                <Form.Label style={styles.label}>Field Name</Form.Label>
                                <Form.Control
                                    as="select"
                                    value={data.fieldName}
                                    onChange={() => data.fieldName}
                                >
                                    <option>Aaaa</option>
                                    <option>Bbb</option>
                                    <option>Ccc</option>
                                </Form.Control>
                            </Form.Group>
                            <Form.Group as={Col} lg={7} md={7} sm={12} xs={12}>
                                <Form.Label style={styles.label}>Field Value </Form.Label>
                                <InputGroup>
                                    <Form.Control
                                        type="text"
                                        placeholder="Enter Value"
                                        name="sku"
                                        value={data.fieldValue}
                                        onChange={() => data.fieldValue}
                                    />
                                </InputGroup>
                            </Form.Group>
                            {/* <Button variant="outline-primary" size="sm" block style={{ width: '100px' }}
                                    onClick={() => props.update(index, data.fieldName, data.fieldValue)}> update</Button> */}

                            <Button variant="outline-primary" size="sm" block style={{ width: '100px' }}
                                onClick={() => props.delete(index)}> delete</Button>
                        </Form.Row>
                    )}
                </div>
                <Form.Row style={{ margin: '0.5% 1%', padding: '1% 2%', background: 'lightGray' }}>
                    <Form.Group as={Col} lg={5} md={5} sm={12} xs={12}>
                        <Form.Label style={styles.label}>Field Name</Form.Label>
                        <Form.Control
                            as="select"
                            value={props.name}
                            onChange={props.fieldNameHandler}
                        >
                            <option>Select</option>
                            <option>Aaaa</option>
                            <option>Bbb</option>
                            <option>Ccc</option>
                        </Form.Control>
                    </Form.Group>
                    <Form.Group as={Col} lg={7} md={7} sm={12} xs={12}>
                        <Form.Label style={styles.label}>Field Value </Form.Label>
                        <InputGroup>
                            <Form.Control
                                type="text"
                                placeholder="Enter Value"
                                name="sku"
                                value={props.value}
                                onChange={props.fieldValueHandler}
                            />
                        </InputGroup>
                    </Form.Group>
                    <Form.Label style={styles.label}><span style={{ color: 'red' }}>
                        {props.error}</span>
                    </Form.Label>
                    <Button variant="outline-primary" size="sm" block style={{ margin: '2% 0px' }}
                        onClick={props.addFieldHandler}>Add Field</Button>
                </Form.Row>
            </Card.Body>
        </Card>

    )
}

const styles = {
    row: {
        margin: '2%',
        padding: '0%'
    },
    card: {
        width: '100%'
    },
    card_header: {
        alignItems: 'center',
        // color: '#6A7074',
        fontSize: '15px',
        background: 'lightgray'
    },
    label: {
        fontSize: '13px',
    }
}
export default CustomFields;
