import React, { Component } from 'react';
import Router from 'next/router'
import DashboardTabs from './components/admin/dashboard/dashboard-tabs';
import DashboardSideDrawer from './components/admin/dashboard/dashboard-side-drawer';
import AdminLayout from './components/admin/layout/AdminLayout';
import GlobalStyleSheet from '../styleSheet';
import { chectAuth, removeTokenFromStorage } from '../sdk/core/authentication-service';

const BackDrop = props => (
    <div>
        <div onClick={props.click}>
        </div>
        <style jsx>{`
             position: fixed;
             width: 100%;
             height: 100%;
             top: 0;
             left: 0;
             background: rgba(0, 0, 0, 0.3);
             z-index: 100;
        `}</style>
    </div>
)

class AdminDashboard extends Component {
    constructor(props) {
        super(props);
        this.authUser()
        this.state = {
            sideDrawerOpen: false,
            showWrapper: true,
            jwt_token: '',
        }
    }

    async authUser() {
        this.setState({ jwt_token: await chectAuth('admin') });
    }

    drawerToggleClickHandler = () => {
        this.setState(prevState => {
            return { sideDrawerOpen: !prevState.sideDrawerOpen };
        });
    };
    ShowWrapperClickHandler = () => {
        this.setState(prevState => {
            return { showWrapper: !prevState.showWrapper };
        });
    };

    backdropClickHandler = () => {
        this.setState({ sideDrawerOpen: false });
    };

    logout() {
        removeTokenFromStorage();
    }

    render() {
        let backdrop;
        if (this.state.sideDrawerOpen) {
            backdrop = <BackDrop click={this.backdropClickHandler} />;
        }

        return (
            <div style={styles.body}>
                {/* <AdminLayout> */}
                <DashboardTabs token={this.state.jwt_token} show={this.state.showWrapper} drawerClickHandler={this.drawerToggleClickHandler}
                    ClickHandler={this.ShowWrapperClickHandler} logoutClickHandler={this.logout} />
                <DashboardSideDrawer token={this.state.jwt_token} show={this.state.sideDrawerOpen}
                    click={this.backdropClickHandler} logoutClickHandler={this.logout} />
                {backdrop}
                {/* </AdminLayout> */}
            </div>
        );
    }
}

const styles = {
    body: {
        background: `${GlobalStyleSheet.body_color}`,
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        minHeight: '100vh',
    },
}

export default AdminDashboard;
