import React, { Component } from 'react';
import { Row, Table, Form, Button, Nav } from 'react-bootstrap'
import axios from 'axios'
import MuhalikConfig from '../../../../../../sdk/muhalik.config'
import GlobalStyleSheet from '../../../../../../styleSheet'

class AllProducts extends Component {
    constructor(props) {
        super(props);
        this.state = {
            productsArray: [],
        }
    }
    // Getting Product Categories from DB
    async componentDidMount() {
        const url = MuhalikConfig.PATH + '/api/products/';
        try {
            const response = await axios.get(url);
            console.log('data:', response.data.data.docs)
            this.setState({ productsArray: response.data.data.docs })
        } catch (error) {
            console.log('Data Fetching Eror:', error);
        }
    }

    render() {
        return (
            <>
                <Row noGutters style={{ margin: '2%', background: 'white' }}>
                    <Table responsive bordered hover size="sm">
                        <thead>
                            <tr>
                                <th style={{ textAlign: 'center' }}>
                                    <Form.Check type="checkbox" />
                                </th>
                                <th style={{ textAlign: 'center' }}>Name</th>
                                <th style={{ textAlign: 'center' }}>SKU</th>
                                <th style={{ textAlign: 'center' }}>Stock</th>
                                <th style={{ textAlign: 'center' }}>Price</th>
                                <th style={{ textAlign: 'center' }}>Categories</th>
                                <th style={{ textAlign: 'center' }}>Tags</th>
                                <th style={{ textAlign: 'center' }}>Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.productsArray && this.state.productsArray.map((element, index) =>
                                _.isEqual(element.product_type, "simple-product") ?
                                    <tr>
                                        <td align="center" style={styles.label}><Form.Check type="checkbox" /></td>
                                        <td align="center" style={{ display: 'inline', fontSize: '13px' }}>
                                            {element.product_name}
                                            <div className="mr-auto"></div>
                                            <Nav.Link style={{ paddingTop: '0%', paddingBottom: '0%' }}>View</Nav.Link>
                                            <Nav.Link style={{ paddingTop: '0%', paddingBottom: '0%' }}>Edit</Nav.Link>
                                            <Nav.Link style={{ paddingTop: '0%', paddingBottom: '0%' }}>Delete</Nav.Link>
                                        </td>
                                        <td align="center" style={styles.label}>{element.sku ? element.sku : '-'}</td>
                                        <td align="center" style={styles.label}>{element.product_in_stock}</td>
                                        <td align="center" style={styles.label}>{element.product_price}</td>
                                        <td align="center" style={styles.label}>
                                            {element.product_category.map(e =>
                                                e.value + ','
                                            )}
                                        </td>
                                        <td align="center" style={styles.label}>
                                            {_.isEmpty(element.product_tags) ? '-' :
                                                element.product_tags.map(e =>
                                                    e.value + ','
                                                )
                                            }
                                        </td>
                                        <td align="center" style={styles.label}>Date Date Date Date Date DateDate Date Date Date Date DateDate Date Date Date Date Date</td>
                                    </tr>
                                    :
                                    <>
                                        {element.product_variations.map((e, i) =>
                                            <tr>
                                                <td align="center" style={styles.label}>
                                                    <Form.Check type="checkbox" />
                                                </td>
                                                <td align="center" style={{ display: 'flex', fontSize: '13px' }}>
                                                    {element.product_name}
                                                    <div className="mr-auto"></div>
                                                    <Nav.Link style={{ paddingTop: '0%', paddingBottom: '0%' }}>View</Nav.Link>
                                                    <Nav.Link style={{ paddingTop: '0%', paddingBottom: '0%' }}>Edit</Nav.Link>
                                                    <Nav.Link style={{ paddingTop: '0%', paddingBottom: '0%' }}>Delete</Nav.Link>
                                                </td>
                                                <td align="center" style={styles.label}>{element.sku ? element.sku : '-'}</td>

                                                <td align="center" style={styles.label}>{e.item[e.item.length - 2].value}</td>
                                                <td align="center" style={styles.label}>{e.item[e.item.length - 3].value}</td>

                                                <td align="center" style={styles.label}>{element.product_category[0].value}</td>
                                                <td align="center" style={styles.label}>{_.isEmpty(element.product_tags) ? '-' : element.product_tags[0].value}</td>
                                                <td align="center" style={styles.label}>Date</td>
                                            </tr>
                                        )}
                                    </>

                            )}
                        </tbody>
                    </Table>
                </Row >
            </>
        );
    }
}

const styles = {
    // table: {
    //     margin: '2%'
    // },
    label: {
        fontSize: `${GlobalStyleSheet.form_label_fontsize}`
    },
}
export default AllProducts;
