import { Form, Row, Col, Card, InputGroup, Button, Spinner } from 'react-bootstrap'

const ProducCategories = () => {
    const [isLoading, setIsLoading] = React.useState(false);
    const [showToast, setShowToast] = React.useState(false);
    const [categoryValue, setCategoryValue] = React.useState('');
    const [error, setError] = React.useState('');

    return (
        <>
            <Row style={styles.title_row} noGutters>
                <FontAwesomeIcon icon={faPlus} style={styles.title_fontawesome} />
                <div className="mr-auto" style={styles.title}> Add New Category </div>
            </Row>
            <Row noGutters>
                <Card style={styles.card}>
                    <Card.Header>
                        Product Category
                        </Card.Header>
                    <Card.Body style={styles.card_body}>
                        <Form.Group>
                            <InputGroup>
                                <Form.Control
                                    type="text"
                                    size="sm"
                                    placeholder="Enter Category Value"
                                    name="sku"
                                    value={categoryValue}
                                    onChange={this.handleCategoryValueChange}
                                    isInvalid={error}
                                />
                                <Form.Control.Feedback type="invalid">
                                    {error}
                                </Form.Control.Feedback>
                            </InputGroup>
                        </Form.Group>
                        <Form.Group>
                            {/* <p style={styles.label}>Fields with <span> * </span> are mandatory.</p>
                                <p style={styles.label}>For adding new size, color, link: Enter text and hit Enter or Tab key</p> */}
                            <Button type="submit" size="sm" onClick={(e) => { handleSubmit, setIsLoading(true) }} disabled={isLoading} block style={styles.submit_btn}>
                                {isLoading ? 'Uploading' : 'Add Category'}
                                {isLoading ? <Spinner animation="grow" size="sm" /> : null}
                            </Button>
                        </Form.Group>
                    </Card.Body>
                </Card>
            </Row>
        </>
    )
}
const styles = {
    title_row: {
        borderBottom: '1px solid gray',
        padding: '1.5% 4%'
    },
    title_fontawesome: {
        color: 'gray',
        marginRight: '3%',
        width: '26px',
        height: '26px',
        maxHeight: '26px',
        maxWidth: '26px',
    },
    title: {
        color: 'gray'
    },

    card: {
        width: '100%',
        margin: '5%'
    },
    card_body: {
        padding: '5%'
    },
    error: {
        width: '100%',
        textAlign: 'center',
        color: '#DC3545',
        fontSize: '14px',
    },
}
export default ProducCategories
