import { Carousel, Col, Row, ListGroup } from 'react-bootstrap'

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {
    faSearch, faUserPlus, faLanguage, faPowerOff, faUser,
    faCartPlus, faHandsHelping, faPen, faSignOutAlt, faGlobe,
    faLuggageCart, faFileInvoiceDollar, faListAlt, faEdit,
    faStoreAlt, faChevronDown, faChevronRight, faListUl, faShoppingCart, faSignLanguage
} from '@fortawesome/free-solid-svg-icons'
import { faProductHunt } from '@fortawesome/free-brands-svg-icons'
import GlobalStyleSheet from '../../../styleSheet'

const images = [
    { img: 'carousel_img1.jpg' },
    { img: 'carousel_img2.jpg' },
    { img: 'carousel_img3.jpg' },
    { img: 'carousel_img4.jpg' },
    { img: 'hassan.jpg' }
]

const CarouselDiv = (props) => {
    const [index, setIndex] = React.useState(0);
    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    return (
        <>
            <div className='div'>
                <Row className='w-100 bg-white' noGutters >
                    <Carousel activeIndex={index} onSelect={handleSelect} indicators={false} interval='1500'>
                        {images && images.map((element, i) =>
                            <Carousel.Item key={i}>
                                <img
                                    style={{ maxHeight: '400px' }}
                                    className="d-block w-100"
                                    src={element.img}
                                    alt='Slide {i}'
                                />
                                <Carousel.Caption>
                                    <h3>Slide {index + 1} label</h3>
                                    <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                        )}
                    </Carousel>
                </Row>
            </div>
            <style jsx>{`
                .div{
                    padding: 0.1% 5%;
                    background: white;
                    box-shadow: 0px 0px 10px 5px ${GlobalStyleSheet.primry_color} inset;
                }
                .category_list_item {
                    cursor: pointer;
                    width: 98%;
                    display: inline-flex;
                    align-items: center;
                    font-size: 14px;
                    padding: 4% 5%;                        
                    color: gray;
                }
                .category_list_item:hover{
                    z-index: 100;
                    color: #005ce6;
                    border-radius: 2px;                        
                    box-shadow: -1px 0px 10px 1px rgba(0,0,0,0.12);
                }
                @media (max-width: 767px) {
                    .div{
                        padding: 0%;
                        margin: 0%;
                    }
                }
            `}</style>
        </>
    )
}

const styles = {
    categories_fontawesome: {
        color: 'gray',
        width: '15px',
        height: '15px',
        maxHeight: '15px',
        maxWidth: '15px',
    },
    faChevronRight: {
        color: 'gray',
        width: '12px',
        height: '12px',
        maxHeight: '12px',
        maxWidth: '12px',
    },
}
export default CarouselDiv
