import { Navbar, Nav, Form, InputGroup, FormControl, Image, Button, NavDropdown, Card, Dropdown, ButtonGroup } from 'react-bootstrap';
import GlobalStyleSheet from '../../../../styleSheet';
import Link from 'next/link';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faUserPlus, faLanguage, faPowerOff, faUser, faCartPlus, faHandsHelping, faPen, faSignOutAlt, faGlobe, faLuggageCart } from '@fortawesome/free-solid-svg-icons';
import MuhalikConfig from '../../../../sdk/muhalik.config';
import axios from 'axios';
function Toolbar(props) {

    // let dashboard = null;
    // let login = null;

    // if (props.token == 'vendor') {
    //     dashboard = <Nav.Link href='./vendor' style={styles.nav_link}>Vendor dashboard</Nav.Link>
    //     login = <Nav.Link onClick={props.logout} style={styles.nav_link}>
    //         <FontAwesomeIcon icon={faPowerOff} style={styles.nav_fontawesome} />
    //         <div style={styles.link_text}>Logout</div>
    //     </Nav.Link>
    // } else if (props.token == 'admin') {
    //     dashboard = <>
    //         <Nav.Link href='./admin' style={styles.nav_link}> Admin dashboard </Nav.Link>
    //         <Nav.Link href='./vendor' style={styles.nav_link}>Vendor dashboard</Nav.Link>
    //     </>
    //     login = <Nav.Link onClick={props.logout} style={styles.nav_link}>
    //         <FontAwesomeIcon icon={faPowerOff} style={styles.nav_fontawesome} />
    //         <div style={styles.link_text}>Logout</div>
    //     </Nav.Link>
    // } else if (props.token == 'customer') {
    //     login = <Nav.Link onClick={props.logout} style={styles.nav_link}> Logout </Nav.Link>
    // } else {
    //     login = <>
    // <Nav.Link href='./login' style={styles.nav_link}>
    //     <FontAwesomeIcon icon={faUser} style={styles.nav_fontawesome} />
    //     <div style={styles.link_text}>Login/Signup</div>
    // </Nav.Link>
    //         {/* <Nav.Link href='./vendor-signup'> Register_as_Shop </Nav.Link> */}
    //     </>
    // }

    const [all, setAll] = React.useState('');

    return (
        <div>
            <Card>
                <Card.Body>
                    <Navbar>
                        <div style={{ display: 'flex', alignItems: 'center' }}>
                            <Image src="muhalik.jpg" roundedCircle thumbnail fluid style={{ width: '60px', position: 'absolute' }} />
                            <h3 style={{ marginLeft: '50px' }} className="text-center">Muhalik</h3>
                        </div>
                        <InputGroup style={styles.search_div}>
                            <InputGroup.Prepend >
                                <Form.Control
                                    style={styles.search_critera}
                                    as="select"
                                    name="countary"
                                    value={all}
                                    onChange={e => setAll(e.target.value)}
                                >
                                    <option>All</option>
                                    <option>Cloths</option>
                                    <option>Mobiles</option>
                                    <option>Laptops</option>
                                    <option>Electronics</option>
                                </Form.Control>
                            </InputGroup.Prepend>
                            <FormControl style={styles.search_box} placeholder="Search here" />
                            <InputGroup.Append style={styles.append}>
                                <FontAwesomeIcon icon={faSearch} style={styles.search_fontawesome} />
                                <Form.Label style={{ color: 'white', margin: '0px 0px 0px 10px', padding: '0px', fontSize: 'large' }}>
                                    Search
                                    </Form.Label>
                            </InputGroup.Append>
                        </InputGroup>

                        <Nav>
                            <Nav.Link href='./login' style={styles.nav_link}>
                                <FontAwesomeIcon icon={faUser} style={styles.nav_fontawesome} />
                                <FontAwesomeIcon icon={faUserPlus} style={styles.nav_fontawesome} />
                                {/* <div style={{ display: 'flex', flexDirection: 'column' }}> */}
                                <div className='nav_link_text'>Signin/up</div>
                                {/* </div> */}
                            </Nav.Link>
                            <Nav.Link href='#' style={styles.nav_link}>
                                <FontAwesomeIcon icon={faGlobe} style={styles.nav_fontawesome} />
                                <div className='nav_link_text'>Eng</div>
                            </Nav.Link>
                            <Nav.Link href="#" style={styles.nav_link}>
                                <FontAwesomeIcon icon={faLuggageCart} style={styles.nav_cart_fontawesome} />
                                <div className='nav_link_text'>Cart</div>
                            </Nav.Link>

                            <Dropdown as={
                                <Image src="muhalik.jpg" roundedCircle thumbnail fluid style={{ width: '35%', maxWidth: '200px' }} />
                            } alignRight
                            >
                                <Dropdown.Toggle size="sm" split variant="light" id="dropdown-split" style={{ background: 'none', border: 'none' }} />
                                <Dropdown.Menu>
                                    <Dropdown.Item href="#/action-1">
                                        <FontAwesomeIcon icon={faUser} style={styles.dropDown_fontawesome} />
                                        My Profile
                                    </Dropdown.Item>
                                    <Dropdown.Item href="#/action-2">
                                        <FontAwesomeIcon icon={faSearch} style={styles.dropDown_fontawesome} />
                                        Feedback
                                    </Dropdown.Item>
                                    <Dropdown.Item href="#/action-3">
                                        <FontAwesomeIcon icon={faHandsHelping} style={styles.dropDown_fontawesome} />
                                        Help?
                                    </Dropdown.Item>
                                    <Dropdown.Divider />
                                    <Dropdown.Item onClick={props.logoutClickHandler}>
                                        <FontAwesomeIcon icon={faPowerOff} style={styles.dropDown_fontawesome} />
                                        Logout
                                </Dropdown.Item>
                                </Dropdown.Menu>
                            </Dropdown>
                        </Nav>
                        {/* <Nav pullRight>
                            <NavDropdown eventKey={1}
                                title={
                                    <div className="pull-left">
                                        {'Mudassir'}
                                    </div>
                                }
                                id="basic-nav-dropdown">

                                <NavDropdown.Item eventKey={1.1} href="/profile">Profile</NavDropdown.Item>
                                <NavDropdown.Item divider />
                                <NavDropdown.Item eventKey={1.3}>
                                    <i className="fa fa-sign-out"></i> Logout
                                </NavDropdown.Item>
                            </NavDropdown>
                        </Nav> */}
                    </Navbar>
                </Card.Body>
            </Card>
            <style jsx>
                {`
                    .nav_link_text {
                        text-align: center;
                        white-space: nowrap;
                        overflow: hidden;
                        font-size: 13px;
                    }

                    .nav_link_text:hover {
                        color: ${GlobalStyleSheet.primry_color}
                    }

                `}
            </style>
        </div>
    )
}

Toolbar.getInitialProps = async ctx => {
    const url = MuhalikConfig.PATH + '/api/categories/categories';
    try {
        const response = await axios.get(url);
        // this.setState({ categoryList: response.data.data });
        return { searchCategory: response.data.data }
    } catch (error) {
        console.error(error);
        return null;
    }
    // const res = await fetch('')
    // const json = await res.json()

}

const styles = {
    background: {
        background: `${GlobalStyleSheet.primry_color}`,
    },
    brand: {
        color: `${GlobalStyleSheet.primry_color}`,
        fontSize: 'medium'
    },
    form: {
        border: `2px solid ${GlobalStyleSheet.primry_color}`,
        width: '60%',
        borderRadius: '25px',
        padding: '0px 5px'
    },
    search_div: {
        margin: '0% 5% 0% 7%',
        // border: `1px solid ${GlobalStyleSheet.primry_color}`,
    },
    search_critera: {
        // background: `${GlobalStyleSheet.primry_color}`,
        border: `2px solid ${GlobalStyleSheet.primry_color}`,
        color: 'black',
        padding: '0px 10px',
        display: 'flex',
        textAlign: 'center',
        alignItems: 'center',
        borderTopLeftRadius: '20px',
        borderBottomLeftRadius: '20px',
    },
    fontawesome: {
        color: `${GlobalStyleSheet.primry_color}`,
