                                </Nav.Link>
                                <Nav.Link href='#' className='p-2'>
                                    <FontAwesomeIcon icon={faFileInvoiceDollar} style={styles.small_fontawesome} />
                                </Nav.Link>
                                <Nav.Link href="#" className='p-2' >
                                    <FontAwesomeIcon icon={faCartPlus} style={styles.small_fontawesome} />
