                                <div className='categories_lg_md'>
                                    <Dropdown onMouseOver={onMouseEnter}
                                        onMouseLeave={onMouseLeave} show={isCategoryOpen}>
                                        <Dropdown.Toggle as={Nav.Link} className="d-inline-flex align-items-center">
