                                            <FontAwesomeIcon icon={faStoreAlt} style={styles.second_nav_fontawesome} />
                                            <div className='second_nav_link_text'>Shops</div>
                                            <div className='width' style={{ width: '70px' }}></div>
                                        </Nav.Link>
                                    </OverlyPopover>
