import React, { Component } from 'react';
import Link from 'next/link';
import axios from 'axios';
import { Dropdown, ButtonGroup, SplitButton, DropdownButton, FormControl, Form, Col, Row, Card, InputGroup, Button, Image } from 'react-bootstrap';
import { Formik } from 'formik';
import * as yup from 'yup';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEye, faEyeSlash } from '@fortawesome/free-solid-svg-icons';

import GlobalStyleSheet from '../../../../../styleSheet';
import MuhalikConfig from '../../../../../sdk/muhalik.config';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import CreatableSelect from 'react-select/creatable';

const animatedComponents = makeAnimated();
const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
]

const schema = yup.object({
    product_name: yup.string().required("Enter Product Name")
        .min(5, "Must have at least 5 characters")
        .max(50, "Can't be longer than 20 characters"),
    brand_name: yup.string()
        .min(5, "Must have at least 5 characters")
        .max(20, "Can't be longer than 20 characters"),
    product_size: yup.string().required("Enter Verification Code"),
    product_color: yup.string().required("Enter Product Color")
        .max(100, "Must be less than 100 characters"),
    product_price: yup.number().required('Enter Product Price')
        .integer("Enter Only Numbers")
        .positive('Enter Between 1-1000000')
        .max(1000000, 'Enter Between 1-1000000'),
    product_in_stock: yup.string().required("Enter Products in Stock")
        .max(20, "Can't be longer than 20 characters"),
    product_shipping_price: yup.number().integer("Enter Only Numbers")
        .min(0, 'Enter Between 0-100')
        .max(100, 'Enter Between 0-100'),
    product_category: yup.string().required("Select Product Category"),
    product_warranty: yup.number().integer("Enter Only Numbers")
        .min(0, 'Enter Between 0-200')
        .max(200, 'Enter Between 0-200'),
    warranty_type: yup.string(),
    product_discount: yup.number().integer("Enter Only Numbers")
        .min(0, 'Enter Between 0-100')
        .max(100, 'Enter Between 0-100'),
    product_description: yup.string()
        .min(5, "Must have at least 5 characters")
        .max(50, "Can't be longer than 20 characters"),
    product_image: yup.string().required("Enter Product Image(s) Link"),
});

class Products extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hide: true,
            product_categories: [],
        };
    }

    handleChange = (newValue, actionMeta) => {
        console.group('Value Changed');
        console.log(newValue);
        console.log(`action: ${actionMeta.action}`);
        console.groupEnd();
    };

    async componentDidMount() {
        const url = MuhalikConfig.PATH + '/api/products-categories/get-all';
        try {
            const response = await axios.get(url);
            this.setState({ product_categories: response.data.data.docs })
        } catch (error) {
            console.error(error);
        }
    }

    handleCategory = (arr) => {
        console.log(arr);
    }

    showPassword = ev => {
        this.setState({ hide: !this.state.hide })
    }

    uploadData(data) {
        const url = MuhalikConfig.PATH + '/api/products/add';
        axios.post(url, {
            data
        }).then(function (response) {
            alert(response.data.message);
        }).catch(function (error) {
            alert(error);
        });
    }

    render() {
        
        return (
            <Formik
                validationSchema={schema}
                initialValues={{
                    product_name: '', brand_name: '',product_price: '', 
                    product_size: '', product_color: '', product_category: '',
                    product_description: '', product_shipping_price: 0,
                    product_discount: 0, product_warranty: 0, product_in_stock: 1
                }}
                onSubmit={(values, { setSubmitting, resetForm }) => {
                    // When button submits form and form is in the process of submitting, submit button is disabled
                    setSubmitting(true);
                    // Resets form after submission is complete
                    resetForm();
                    // Sets setSubmitting to false after form is reset
                    setSubmitting(false);

                    setTimeout(() => {
                        this.uploadData(values);
                        resetForm();
                        setSubmitting(false);
                    }, 500);
                }}
            >
                {
                    ({
                        handleSubmit,
                        handleChange,
                        values,
                        touched,
                        isValid,
                        errors,
                        handleBlur,
                        isSubmitting
                    }) => (
                            <div style={styles.body}>
                                <Navbar variant="dark" style={{ background: `${GlobalStyleSheet.primry_color}` }}>
                                    <Navbar.Brand href="/" className="mr-auto" > Muhalik </Navbar.Brand>
                                </Navbar>

                                <Container>
                                    <Row>
                                        <Col lg={1} md={1} sm={0} xs={0} style={styles.side_column}></Col>
                                        <Col style={styles.center_column}>
                                            <p>
                                                <Image src="muhalik.jpg" roundedCircle thumbnail fluid style={{ width: '25%', maxWidth: '150px' }} />
                                            </p>
                                            <h6 className="text-center" style={{ width: '100%', paddingBottom: '10px' }}>Create Your Acount</h6>
                                            <Form noValidate onSubmit={handleSubmit}>
                                                <Form.Row>
                                                    <Form.Group as={Col} md="6" controlId="validationMobile">
                                                        <Form.Label style={styles.label}>Mobile Number <span>*</span></Form.Label>
                                                        <InputGroup>
                                                            <Form.Control
                                                                type="text"
                                                                placeholder="+966590911891"
                                                                aria-describedby="mobile"
                                                                name="mobile"
                                                                value={values.mobile}
                                                                onChange={handleChange}
                                                                isInvalid={touched.mobile && errors.mobile}
                                                            />
                                                            <InputGroup.Prepend>
                                                                <Button id="sndCodeBtn" style={styles.buttons}>Send Code</Button>
                                                            </InputGroup.Prepend>
                                                            <Form.Control.Feedback type="invalid">
                                                                {errors.mobile}
                                                            </Form.Control.Feedback>
                                                        </InputGroup>
                                                    </Form.Group>
                                                    <Form.Group as={Col} md="6" controlId="validationFullName">
                                                        <Form.Label style={styles.label}>Full Name<span>*</span></Form.Label>
                                                        <InputGroup>
                                                            <Form.Control
                                                                type="text"
                                                                placeholder="Full Name"
                                                                aria-describedby="fullName"
                                                                name="fullName"
                                                                value={values.fullName}
                                                                onChange={handleChange}
                                                                isInvalid={touched.fullName && errors.fullName}
                                                            />
                                                            <Form.Control.Feedback type="invalid">
                                                                {errors.fullName}
                                                            </Form.Control.Feedback>
                                                        </InputGroup>
                                                    </Form.Group>
                                                </Form.Row>

                                                <Form.Row>
                                                    <Form.Group as={Col} md="6" controlId="validationVerificationCode">
                                                        <Form.Label style={styles.label}>Verification Code
                                                        <span> * </span></Form.Label>
                                                        <Form.Control
                                                            type="text"
                                                            placeholder="Verification Code"
                                                            name="verificationCode"
                                                            value={values.verificationCode}
                                                            onChange={handleChange}
                                                            isInvalid={touched.verificationCode && errors.verificationCode}
                                                        />
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.verificationCode}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>
                                                    <Form.Group as={Col} md="6" controlId="validationEmail">
                                                        <Form.Label style={styles.label}>Email Address</Form.Label>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder="mr.x@gmail.com"
                                                            name="email"
                                                            value={values.email}
                                                            onChange={handleChange}
                                                            isInvalid={touched.email && errors.email}
                                                        />
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.email}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>
                                                </Form.Row>

                                                <Form.Row>
                                                    <Form.Group as={Col} md={6} controlId="validationPassword">
                                                        <Form.Label style={styles.label}>Password <span>*</span></Form.Label>
                                                        <InputGroup>
                                                            <Form.Control
                                                                type={hide ? 'password' : 'text'}
                                                                placeholder="Enter Password"
                                                                aria-describedby="inputGroup"
                                                                name="password"
                                                                value={values.password}
                                                                onChange={handleChange}
                                                                isInvalid={touched.password && errors.password}
                                                            />
                                                            <InputGroup.Prepend>
                                                                <Button id="passwordEyeBtn" onClick={this.showPassword} style={styles.buttons}>
                                                                    {eyeBtn}
                                                                </Button>
                                                            </InputGroup.Prepend>
                                                            <Form.Control.Feedback type="invalid">
                                                                {errors.password}
                                                            </Form.Control.Feedback>
                                                        </InputGroup>
                                                    </Form.Group>
                                                    <Form.Group as={Col} md={6} controlId="validationConfirmPassword">
                                                        <Form.Label style={styles.label}>Confirm Password <span>*</span></Form.Label>
                                                        <InputGroup>
                                                            <Form.Control
                                                                type={hide ? 'password' : 'text'}
                                                                placeholder="Re-enter Password"
                                                                aria-describedby="confirmPassword"
                                                                name="confirmPassword"
                                                                value={values.confirmPassword}
                                                                onChange={handleChange}
                                                                isInvalid={touched.confirmPassword && errors.confirmPassword}
                                                            />
                                                            <InputGroup.Prepend>
                                                                <Button id="confirmPasswordEyeBtn" onClick={this.showPassword} style={styles.buttons}>
                                                                    {eyeBtn}
                                                                </Button>
                                                            </InputGroup.Prepend>
                                                            <Form.Control.Feedback type="invalid">
                                                                {errors.confirmPassword}
                                                            </Form.Control.Feedback>
                                                        </InputGroup>
                                                    </Form.Group>
                                                </Form.Row>


                                                {/* 4th Row */}
                                                <Form.Row>
                                                    <Form.Group as={Col} lg={2} md={3} controlId="countary">
                                                        <Form.Label style={styles.label}>Countary
                                                        <span> * </span>
                                                        </Form.Label>
                                                        <Form.Control
                                                            as="select"
                                                            aria-describedby="countary"
                                                            name="countary"
                                                            value={values.countary}
                                                            onChange={handleChange}
                                                            isInvalid={touched.countary && errors.countary}

                                                        >
                                                            <option>Select</option>
                                                            <option> KSA </option>
                                                            <option> Pak </option>
                                                        </Form.Control>
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.countary}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>
                                                    <Form.Group as={Col} lg={2} md={3} controlId="gender">
                                                        <Form.Label style={styles.label}>Gender
                                                        <span> * </span>
                                                        </Form.Label>
                                                        <Form.Control
                                                            as="select"
                                                            aria-describedby="gender"
                                                            name="gender"
                                                            value={values.gender}
                                                            onChange={handleChange}
                                                            isInvalid={touched.gender && errors.gender}
                                                        >
                                                            <option>Select</option>
                                                            <option> Male </option>
                                                            <option> Female </option>
                                                            <option> Other </option>
                                                        </Form.Control>
                                                        <Form.Control.Feedback type="invalid">
                                                            {errors.gender}
                                                        </Form.Control.Feedback>
                                                    </Form.Group>
                                                    <Col>
                                                        <Form.Group >
                                                            <Form.Label style={{ color: 'white', fontSize: '0.1px' }}> . </Form.Label>
                                                            <Form.Label className="text-center" style={styles.term_condition_label}>
                                                                By creating acount, you agree to Muhalik's
                                                                <span>
                                                                    <Link href="./help/terms-and-conditions"><a>Terms & Conditions</a></Link>
                                                                </span>
                                                                and
                                                                <span>
                                                                    <Link href="./help/privacy-statement"><a>Privacy Statement</a></Link>
                                                                </span>
                                                            </Form.Label>
                                                        </Form.Group>
                                                    </Col>
                                                    <Form.Group as={Col} lg={4} controlId="loginGrop">
                                                        <Form.Label className="text-center" style={styles.label}>
                                                            Already have an account...
                                                            <span>
                                                                <Link href="login"><a>Login</a></Link>
                                                            </span>
                                                        </Form.Label>
                                                        <Button type="submit" onSubmit={handleSubmit} block style={styles.submit_btn}>Signup</Button>
                                                    </Form.Group>
                                                </Form.Row>
                                                {/* End 4th Row */}
                                            </Form>
                                        </Col>
                                        <Col lg={1} md={1} sm={0} xs={0} style={styles.side_column}></Col>
                                    </Row>
                                </Container>
                                <style jsx>
                                    {`
                                        span {
                                            color: red;
                                        }
                                        p {
                                            text-align: center; 
                                            margin: 0px;
                                        }
                                    `}
                                </style>
                            </div>
                        )}
            </Formik>
        );
    }
}

const styles = {
    body: {
        background: `${GlobalStyleSheet.body_color}`,
        position: 'absolute',
        top: '0',
        left: '0',
        right: '0',
        bottom: '-100',

    },
    buttons: {
        background: `${GlobalStyleSheet.primry_color}`,
        border: 'none',
        fontSize: '10px',
    },
    submit_btn: {
        background: `${GlobalStyleSheet.primry_color}`,
    },
    center_column: {
        background: 'white',
        // border: `0.5px solid ${GlobalStyleSheet.primry_color}`,
        padding: '20px 30px',
        margin: '3% 3%',
    },
    side_column: {
        margin: '0 2%',
    },
    label: {
        width: '100%',
        fontSize: `${GlobalStyleSheet.form_label_fontsize}`,
    },
    term_condition_label: {
        width: '100%',
        fontSize: `${GlobalStyleSheet.form_label_fontsize}`,
        paddingTop: '-10px',
        marginTop: '-10px',
    },
    fontawesome: {
        color: `${GlobalStyleSheet.primary_text_color}`,
        width: '15px',
        height: '15px',
        maxHeight: '15px',
        maxWidth: '15px',
    },
}

export default Products;
